import './App.css';
import React, { Component } from 'react';
import Board from './components/Board/Board';
import Moves from './components/Moves/Moves';

class App extends Component {

  constructor(){
    super();
    this.state = {
      moveHistory: [],
      currentMove: 0,
      colorToMove: 1
    };
  }

  dropHandler = (e) => {
    e.preventDefault();
    let target = e.target.dataset.position; 
    let origin = e.dataTransfer.getData("text/plain");
    console.log(`origin: ${origin}, target: ${target}`);
    this.movePiece(origin, target)
  };

  movePiece(from, to){
    let moveHistory_copy = [...this.state.moveHistory];
    let newMove = [from ,to];
    let colorToMove = this.state.colorToMove;

    if(colorToMove===1){
      moveHistory_copy.push([newMove]); 
      colorToMove=-1;
    }else if(colorToMove===-1){
      moveHistory_copy[moveHistory_copy.length-1].push(newMove); 
      colorToMove = 1;
    }
    this.setState(prevState => ({
      moveHistory: moveHistory_copy, 
      currentMove: newMove,
      colorToMove: colorToMove
    }));
  }



  render(){
    return(
    	<div className='container'>
	    	<div className="board"> 
	    		<Board onDrop={this.dropHandler} moveHistory={this.state.moveHistory} currentMove={this.state.currentMove} />
      		</div>
      		<div className='moves' >
      			<Moves moveHistory={this.state.moveHistory} /> 
      		</div>
      	</div> 
    );
  }


}

export default App;
