	initSquares = () => {
		let squares = {};
		let r = 8;
		let c = 1; 
		let bgs = ['black', 'white'];
		let tick = 0; 
		for(let i =0;i<64;i++){
			let pos = Number(''+c+r);
			if(pos in startingPosition){
				squares[pos] = 
				{
					key: ''+c+r,
					row: r,
					column: c,
					background: bgs[tick],
					piece: startingPosition[pos] 
				};
				// (<Square key={''+c+r} 
				// 	pieceClick={3}
