import React from 'react'; 
import './Squares.css';
import Piece from '../Piece/Piece';


class Squares extends React.Component{
	constructor(props){
		super(props);
		this.state = {
			piece_clicked: false
		}
	}

	render(){
		let {position, onClick, pClick, onDrop, dragStart, dragEnd, colorToMove} = this.props;
		return (
			<div className='board'> 
				{
					position.map((element, i)=>{
						return element ===0 
						? <div 
							id={'s'+i}
							onDragOver={this.dragOverHandler} 
							onDragLeave={this.dragLeaveHandler} 
							onClick={()=>this.squareClick(i, onClick)} 
							onDrop={(e)=>onDrop(e, i)} 
							key={'square'+i} 
							className='square' 
							/>
						: <div 
							onDragOver={this.dragOverHandler} 
							onDragLeave={this.dragLeaveHandler} 
							onDrop={(e)=>onDrop(e, i)} 
							id={'s'+i}
							key={'square'+i} 
							className='square'> 
								<Piece 
									position={i}
									dragStart={dragStart} dragEnd={dragEnd}
									onClick={(e)=>this.pieceClick(e, pClick, i)} 
									piece={element} 
									colorToMove={colorToMove}
									/> 
							</div>; 
					})
				}
			</div> 
		);
	}

	getPieceColor(piece){
		return piece<0 ? -1 : 1; 
	}

	pieceClick(e, callback, i){
		console.log(e, callback, i);
		if(!this.state.piece_clicked){
			e.target.parentElement.classList.add('active');
			this.setState({piece_clicked: e.target});
		}
		else{
			this.state.piece_clicked.parentElement.classList.remove('active'); 
			this.setState({piece_clicked: false});
		}
		callback(i);
	}

	squareClick(square, onClick){
		if(!!(this.state.piece_clicked)){
			this.state.piece_clicked.parentElement.classList.remove('active'); 
			this.setState({piece_clicked: false});
		}
		onClick(square);
	}

	dragOverHandler = (e) => {
		e.preventDefault(e);
		requestAnimationFrame(function () {
			e.target.classList.add('dragover');
		});
	}

	dragLeaveHandler = (e) => {
		e.preventDefault(); 
		requestAnimationFrame(function () {
			e.target.classList.remove('dragover');
		}); 
	}
}
export default Squares;
