import React from 'react'; 
import './Squares.css';
import Piece from '../Piece/Piece';


class Squares extends React.Component{
	constructor(props){
		super(props);
		this.state = {
			position: this.props.position
		}
	}

	render(){
		let position = this.state.position;
		let {onClick, pClick, onDrop} = this.props;
		return (
			<div className='board'> 
				{
					position.map((element, i)=>{
						if(element===0) return <div onDragOver={this.dragOverHandler} onDragLeave={this.dragLeaveHandler} onClick={()=>this.squareClick(i, onClick)} onDrop={(e)=>onDrop(e, i)} key={'square'+i} className='square' />;
						else return 
							<div onDragOver={this.dragOverHandler} onDragLeave={this.dragLeaveHandler} onClick={()=>this.squareClick(i, onClick)} onDrop={(e)=>onDrop(e, i)} key={'square'+i} className='square'> 
								<Piece position={i} onClick={pClick} piece={element} /> 
							</div>; 
					})
				}
			</div> 
		);
	}

	squareClick(square, onClick){
		onClick(square);
	}

	dragOverHandler = (e) => {
		e.preventDefault(e);
		requestAnimationFrame(function () {
			e.target.classList.add('dragover');
		});
	}


	dragLeaveHandler = (e) => {
		e.preventDefault(); 
		requestAnimationFrame(function () {
			e.target.classList.remove('dragover');
		}); 
	}


}
export default Squares;
