import React, { Component } from 'react';
import './Board.css'; 
import Square from '../Square/Square';



// TYPES 
// PAWN - 0 
// BISHOP - 1 
// KNIGHT - 2 
// ROOK - 3 
// QUEEN - 4 
// KING - 5 


const startingPosition = 
{
	//PAWNS position: [type, color]
	12: [0, 1], 22: [0, 1], 32: [0, 1], 42: [0, 1], 52: [0, 1], 62: [0, 1], 72: [0, 1], 82: [0, 1],
	17: [0, -1], 27: [0, -1], 37: [0, -1], 47: [0, -1], 57: [0, -1], 67: [0, -1], 77: [0, -1], 87: [0, -1],

	18: [3, -1], 11: [3, 1], 88: [3, -1], 81: [3, 1], // ROOKS 
	28: [2, -1], 31: [2,1], 71:[2,1], 78: [2,-1], // KNIGHTS 
	38: [1,-1], 68: [1,-1], 21: [1,1], 61: [1,1], // BISHOPS 
	41: [4,1], 48: [4,-1], //QUEENS 
	51: [5,1], 58: [5,-1] //KINGS 
};


class Board extends Component {
	constructor(props){
		super(props);
		this.state = {
			squares: this.initSquares(),
			handleClick: this.handleClick,
			handlePieceClick: this.handlePieceClick,
			render: this.renderSquares()
		};
	}

	handleClick = (e) => {
		console.log("square", e);
		// console.log(this.state.squares[e]);
	};

	handlePieceClick = (e) => {
		console.log("piece", e);
	};

	// movePiece = (start, end) => {
	// 	squares.start
	// };



	initSquares = () => {
		let squares = {};
		let r = 8;
		let c = 1; 
		let bgs = ['black', 'white'];
		let tick = 0; 
		for(let i =0;i<64;i++){
			let pos = Number(''+c+r);
			if(pos in startingPosition){
				squares[pos] = 
				{
					'key': ''+c+r,
					'row': r,
					'column': c,
					'background': bgs[tick],
					'piece': startingPosition[pos] 
				};
				// (<Square key={''+c+r} 
				// 	pieceClick={3}
				// 	// pieceClick = {() => this.handlePieceClick(pos)} 
				// 	// onClick={() => this.handleClick(pos)} 
				// 	row={r} column={c} background={bgs[tick]} piece={startingPosition[pos]} />);
			} 
			else 
				squares[pos] = {
					'key': ''+c+r,
					'row': r,
					'column': c,
					'background': bgs[tick],
					'piece': null 
				};
				// squares[pos] = (<Square key={''+c+r} onClick={() => this.handleClick(pos)} row={r} piece={null} column={c} background={bgs[tick]}  />);
			c++; 
			if(c>8){
				c = 1; 
				r--;
			}else{
				tick = tick === 0 ? 1 : 0;
			}
		}
		return squares; 
	};

	renderSquares = () => {
		let squares = this.state.squares;
		let render = []; 
		// Object.keys(square).forEach(e=>{
		// 	render.push(<Square key={e.key} row={e.row} column={e.column} background={e.background} piece={e.piee} />);
		// });
		for (let key of Object.entries(squares)) {
			let curr = squares.key; 
			render.push(<Square key={curr.key} row={curr.row} column={curr.column} background={curr.background} piece={curr.piee} />);
		}
		return render; 
	};

	render(){
		console.log(this.state.squares);
		console.log("render", this.renderSquares());
		return (
			<div id="board"> 
				{this.renderSquares()}
			</div>
		);
	}
}
export default Board;
