import React from 'react'; 
import './Piece.css';
import Draggable from 'react-draggable';

const typeConv = {
	1: 'pawn', 2:'bishop', 3:'knight', 4:'rook', 5:'queen', 6:'king'
}

	// <Piece position={index} 
	// 			// onClick={() => availableMoves(piece[0])}
	// 			onDragStart={dragStartHandler}
	// 			onDragEnd={dragEndHandler}
	// 			piece={piece} 

let colorConv = number => {
	if(number>0) return 'white';
	else return 'black'; 
}




const Piece = ({position, piece, onClick, dragStart, dragEnd, colorToMove}) => {
	let pieceClass = `piece ${typeConv[Math.abs(piece)]} ${colorConv(piece)}`;
	// let key = `p${position}`;
	let properties = 
		{
			'className': pieceClass
		};
	if(colorToMove===piece) properties = {...properties, onDragEnd:dragEnd, onDragStart: (e)=>dragStart(e, position), onClick: onClick, draggable: 'true'}
	return React.createElement('div', properties);
	// <div className={pieceClass} onDragEnd={dragEnd} onDragStart={(e) => dragStart(e, position)} onClick={onClick} draggable="true" />;
}

export default Piece;
