import React, {Component} from 'react'; 
import './Moves.css';


// let boardConv = [];	

class Moves extends Component {

	constructor(props){
		super(props);
		let {moveHistory} = props;  // moveHistory: [[from, to], [from, to], [from, to], ...]
		// this.newMoveBlack = this.newMoveBlack.bind(this);
		this.state = {
			 render: [],
			 playedMoves: 0
		}
	}

	numberOfRows(length){
		return Math.ceil(length/2);
	}

	newMoveWhite(move, move_number){
		let row_number = React.createElement('div', {className:'move_number',key:`move_number${move_number}`}, move_number);
		let white = React.createElement('div', {className:'move_white', key: `move_white${move_number}`},move);
		return [row_number, white];
	}
	newMoveBlack(move, move_number){
		let black = React.createElement('div', {className:'move_black', key: `move_black${move_number}`},move);
		return black;
	}

	squareConv=(square)=>{

	} 

	render(){
		let history = this.props.moveHistory;
		let render = [];
		let move_number = 1; 
		history.forEach((element,index)=>{
			if(index%2===0){
				render.push(this.newMoveWhite(element, move_number));
				move_number++;
			}else render.push(this.newMoveBlack(element)); 
		});


		return (
			<div id="moves"> 
				<div id="top_row"> Moves </div>
				<div id="moves_table"> 
					{render}
				</div>
			</div>
		)
	}
}

export default Moves;
