import React from 'react'; 
import './Square.css';
import Piece from '../Piece/Piece';
// import Draggable from 'react-draggable';

const original_rank = {
	'0': 2, '-1': 7
}

const dragOverHandler = (e) => {
	e.preventDefault(e);
	requestAnimationFrame(function () {
		e.target.classList.add('dragover');
	});
};

const dragStartHandler = (e) => {
	e.dataTransfer.dropEffect = "move";
	e.dataTransfer.setData("text/plain", e.target.dataset.position); 
	requestAnimationFrame(function () {
		e.target.classList.add('hide');
	});
};

const dragLeaveHandler = (e) => {
	e.preventDefault(); 
	requestAnimationFrame(function () {
		e.target.classList.remove('dragover');
	}); 
};

const dragEndHandler = (e) =>{
	e.preventDefault();
	let element = e.target;
	  	requestAnimationFrame(function () {
    	element.classList.remove('hide');
  	});

};




function Square({row, column, background, piece, onClick, pieceClick, onDrop}){
	let classes = 'square '+background;
	let position = Number(''+column+row);
	let type = piece[0];

	let availableMoves = () => {
		switch(this.type){
			case 0: 
				console.log('pawn');
				break;

			default: return;
		}
	}
	// if(piece) availableMoves(piece[0]);
	return piece
		? <div className={classes} data-row={row} 
			data-column={column}
			data-position={position}
			onDragOver={dragOverHandler}
			onDragLeave={dragLeaveHandler}
			onDrop={onDrop}
			> 
			<Piece position={position} 
				// onClick={(e) => pieceClick(e, position)}
				onClick={() => availableMoves()}
				onDragStart={dragStartHandler}
				onDragEnd={dragEndHandler}
				type={type} color={piece[1]} /> 
		</div>
		: <div className={classes} 
			onDrop={onDrop}
			data-position={position} 
			onDragLeave={dragLeaveHandler} 
			onDragEnd={dragEndHandler} 
			onDragOver={dragOverHandler} 
			onClick={onClick} 
			data-row={row} 
			data-column={column} />;
}


export default Square;
