import React from 'react'; 
import './Piece.css';

const typeConv = {
	0: 'pawn', 1:'bishop', 2:'knight', 3:'rook', 4:'queen', 5:'king'
}

let colorConv = number => {
	if(number===1) return 'white';
	else if(number===-1) return 'black'; 
	else return null; 
}
const Piece = ({position, type, color}) => {
	let pieceClass = `piece ${typeConv[type]} ${colorConv[color]}`
	return (
		<div className={pieceClass} /> 
	);
}

export default Piece;
