import React from 'react'; 
import './Moves.css';
import ImageButton from './../ImageButton/ImageButton';
import MoveButton from './MoveButton/MoveButton';

// let typeConversion = ['','B','N','R','Q','K'];
// let moveConverter = ["a8", "b8", "c8", "d8", "e8", "f8", "g8", "h8", "a7", "b7", "c7", "d7", "e7", "f7", "g7", "h7", "a6", "b6", "c6", "d6", "e6", "f6", "g6", "h6", "a5", "b5", "c5", "d5", "e5", "f5", "g5", "h5", "a4", "b4", "c4", "d4", "e4", "f4", "g4", "h4", "a3", "b3", "c3", "d3", "e3", "f3", "g3", "h3", "a2", "b2", "c2", "d2", "e2", "f2", "g2", "h2", "a1", "b1", "c1", "d1", "e1", "f1", "g1", "h1"];
// TYPES 
// PAWN - 1
// BISHOP - 2 
// KNIGHT - 3
// ROOK - 4 
// QUEEN - 5 
// KING - 6
const Moves = ({moves, onClick, selected_move, colorToMove, result, children}) => {

	// let colorToText = (color) => {
	// 	return color===1 ? 'white' : 'black'; 
	// }

	function moveClicked(i){
		onClick(i);
	}



	/**


	[moves] => [d4, d5, d2, ex6, f7, ...]

		=> 
		<div class="item">
			<div class="number" />
			<div class="white" /> 
			<div class="black" />
		</div>

	--> Schleife: 
		index 	- wenn i%2 == 0 && i!==0 -  move und number erstellen [[nur das erste Mal!!]]

				- wenn 1%2 == 1 		 - 	move erstellen 

				- wenn i%2 == 0 %% i>0	 -  Parent erstellen, number erstellen und Kinder hinzufügen  

	**/ 



	let render = []; 
	if(moves && moves.length>0){
		// let classes = ['move_number', 'move_white', 'move_black']; 
		let classes = ['move_white', 'move_black']; 
		let tick = 0;
		let count = 1;
		// moves.forEach((element, index) => {	
		// 	if(tick===0){
		// 		render.push(
		// 			React.createElement('div', {className:classes[tick], key:'mr'+count}, count)
		// 		);
		// 		count++;
		// 		tick++; 
		// 	}
		// 	if(index===selected_move-1)
		// 		render.push(
		// 			React.createElement('div', {className:classes[tick]+' selected', id:'m'+index, key:'m'+index, onClick: () => onClick(index+1)}, element)
		// 		); 
		// 	else 
		// 		render.push(
		// 			React.createElement('div', {className:classes[tick], id:'m'+index, key:'m'+index, onClick: () => onClick(index+1)}, element)
		// 		); 

		// 	tick = (tick>=2) ? 0 : tick+1; 
		// });
		let buffer = []; 
		moves.forEach((element, index) => {	
			if(index%2===0){
				if(index>0){
					React.createElement('div', {className:'move_item', key:'mi'+count}, buffer);
					buffer = []; 
				}
				buffer.push(
					React.createElement('div', {className:'move_number', key:'mr'+count}, count)
				);
				count++; 
			}
			buffer.push(
				React.createElement('div', {className:classes[index%2]+index===selected_move-1?' selected':'', id:'m'+index, key:'m'+index, onClick: () => onClick(index+1)}, element)
			); 
		});



	}
	return (
		<div id="moves"> 
			<div id="moves-container">
				<div id="top_row">
					<div className="left">Moves</div>
					<div className="right">
						<span className={'color '+colorToMove} /> to move
					</div>
				</div>

				<div id="table-container">
					<div className="move-button-container">
						<MoveButton onClick={()=>moveClicked(0)} altText="Go to first Move" button="first_move" />
						<MoveButton onClick={()=>moveClicked(selected_move-1)} altText="Go to previous Move" button="prev_move" />
						<MoveButton onClick={()=>moveClicked(selected_move+1)} altText="Go to next Move" button="next_move" />
						<MoveButton onClick={()=>moveClicked(999)} altText="Go to last Move" button="last_move" />
					</div>
					<div className="movesTableContainer">
						<div id="moves_table"> 

							{render
							}

						</div>
					</div>
				</div>
				<div id="bottom">
					{result && 
					<div id="moves_result"> {result} </div> 
					}	
					<div id="button-container"> 
						{children}
					</div>
				</div>
			</div>
		</div>
	)
}

export default Moves;
