import React, {Component} from 'react'; 
import './Moves.css';

let typeConversion = ['','B','N','R','Q','K'];
let moveConverter = ["a8", "b8", "c8", "d8", "e8", "f8", "g8", "h8", "a7", "b7", "c7", "d7", "e7", "f7", "g7", "h7", "a6", "b6", "c6", "d6", "e6", "f6", "g6", "h6", "a5", "b5", "c5", "d5", "e5", "f5", "g5", "h5", "a4", "b4", "c4", "d4", "e4", "f4", "g4", "h4", "a3", "b3", "c3", "d3", "e3", "f3", "g3", "h3", "a2", "b2", "c2", "d2", "e2", "f2", "g2", "h2", "a1", "b1", "c1", "d1", "e1", "f1", "g1", "h1"];
// TYPES 
// PAWN - 1
// BISHOP - 2 
// KNIGHT - 3
// ROOK - 4 
// QUEEN - 5 
// KING - 6
class Moves extends Component {

	constructor(props){
		super(props);
		let {lastMove, onClick} = props;  
		// this.newMoveBlack = this.newMoveBlack.bind(this);
		this.state = {
			 moves: [],
			 moveDisplay: 1
		}
	}

	displayMove(move){

	}

	static getDerivedStateFromProps(props, state) {
		let {lastMove} = props; 
		if(!lastMove||lastMove===undefined) return null;
		let moves = state.moves; 
		moves.push(lastMove);
		return {moves: moves};
	}		

	render(){
		let moves = this.state.moves; 
		console.log(moves);
		let classes = ['move_number', 'move_white', 'move_black']; 
		let render = []; 
		let tick = 0;
		let count = 1;
		moves.forEach((element, index) => {	
			if(tick===0){
				render.push(
					React.createElement('div', {className:classes[tick], key:'mr'+count}, count)
				);
				count++;
				tick++; 
			}	
			render.push(
				React.createElement('div', {className:classes[tick], key:'m'+index}, moveConverter[element])
			); 

			tick = (tick>=2) ? 0 : tick+1; 
		});

		return (
			<div id="moves"> 
				<div id="top_row"> Moves </div>
				<div id="moves_table"> 
					{render
					}
				</div>
			</div>
		)
	}


	numberOfRows(length){
		return Math.ceil(length/2);
	}

	newMoveWhite(move_number, type, move, internal_move){
		let row_number = React.createElement('div', {className:'move_number',key:`move_number${move_number}`}, move_number);
		let white = React.createElement('div', {className:'move_white', key: `move_white${move_number}`, onClick:()=>this.props.onClick(1, internal_move)},typeConversion[type]+this.convertMove(move));
		return [row_number, white];
	}
	newMoveBlack(move_number, type, move, internal_move){
		return React.createElement('div', {className:'move_black', key: `move_black${move_number}`, onClick:()=>this.props.onClick(-1, internal_move)},typeConversion[type]+this.convertMove(move));

	}





}

export default Moves;
