import React from 'react'; 
import './Piece.css';
import Draggable from 'react-draggable';

const typeConv = {
	0: 'pawn', 1:'bishop', 2:'knight', 3:'rook', 4:'queen', 5:'king'
}

// const movement = {
// 	0: 

// 	1: 

// 	2: 

// 	3:

// 	4:

// 	5: 
// }

let colorConv = number => {
	if(number===1) return 'white';
	else if(number===-1) return 'black'; 
	else return null; 
}

const Piece = ({position, type, color, onClick, onDragStart, onDragEnd} ) => {
	let pieceClass = `piece ${typeConv[type]} ${colorConv(color)}`;
	let key = `p${position}`;
	return <div className={pieceClass} onDragStart={onDragStart} data-position={position} onDragEnd={onDragEnd} draggable="true" onClick={onClick} />;
}

export default Piece;
