import React, {Component} from 'react'; 
import './PGNDialog.css';
import GamesList from './../GamesList/GamesList';


class PGNDialog extends Component{

	constructor(props){
		super(props);
		this.state = {
			closePopup: props.closePopup,
			input: '',
			games: ''
		}
	}

	componentDidMount(){
		this.loadStudy(); 
	}

	render(){
		return ( 
			<>
				<h1>PGN</h1> 
				<div className="inputclass">		
					<div className="inputbox"> 
						<textarea 
							placeholder="Enter PGN" 
							onChange={(e) => this.setInput(e.target.value)} 
							value={this.state.input}
							// value="https://lichess.org/game/export/6dFspuKY" 
							id="inputpgn" /> 
					</div>
					<div className="inputsubmit">
						<span 
							id="popupsubmit" 
							className='pointer popup_button' 
							value="submit" 
							onClick={()=>this.closePopup(this.state.input)}>
							Import
						</span>
					</div>
				</div>

				<h1> Famous Games: </h1> 
				<div className="games_list" id="games_list_container">
					{this.state.games}
				</div>

			</>
		);
	}

	setInput(input){
		this.setState({input});
	}
	setGames(games){
		this.setState({games});
	}

	loadStudy = () => {
		fetch('http://localhost:3001/game?all=true')
	      	.then(res => res.json())
	     	.then(res => {
	        	this.setGames(<GamesList games={res} closePopup={this.state.closePopup} />);
        	});
	}


}

export default PGNDialog;
