            } else col_l = false;
          }

          if (row - 1 >= 0) {
            cs = this.convertBack(row - 1, column); // 1 oben

            cp = position[cs];
            if (!this.hasPiece(cp) || this.hasOpposingPiece(cp, piece)) moves.push(cs);

            if (col_r) {
              cs = this.convertBack(row - 1, column + 1); // oben rechts

              cp = position[cs];
              if (!this.hasPiece(cp) || this.hasOpposingPiece(cp, piece)) moves.push(cs);
            }

            if (col_l) {
              cs = this.convertBack(row - 1, column - 1); // oben links

              cp = position[cs];
              if (!this.hasPiece(cp) || this.hasOpposingPiece(cp, piece)) moves.push(cs);
            }
          }

          break;
        }

      case 0:
        return 0;

      default:
        return 0;
    } // console.timeEnd('moves');


    return moves;
  } // @e: dragEvent
  // @i: square of dragged piece 


  getColorToMoveByMoveNumber(number) {
    return number % 2 === 0 ? 1 : -1;
  }

  squaresOfSameColor(one, two) {
    return chess.get(one).color === chess.get(two).color;
  }

  highlightMoves(piece) {
    chess.moves({
      square: piece,
      verbose: true
    }).forEach(move => {
      document.getElementById(move.to).classList.add('moveable');
    });
  }

  attemptMove(from, to) {
    return chess.move({
      from: from,
      to: to
    });
  }

  switchColor() {
    return this.state.colorToMove === 1 ? -1 : 1;
  }

  getColor(piece) {
    return piece < 0 ? -1 : 1;
  }

  getColorFromSquare(square) {
    return this.getColor(this.state.positions[this.state.currentMove][square]);
  }

  hasPiece(piece) {
    return Math.abs(piece) > 0;
  }

  squareHasPiece(square) {
    return this.hasPiece(this.state.positions[this.state.currentMove][square]);
  }

  squareHasOpposingPiece(square, square2) {
    return this.getColorFromSquare(square) !== this.getColorFromSquare(square2);
  }

  hasOpposingPiece(piece1, piece2) {
    return this.hasPiece(piece2) && this.getColor(piece1) !== this.getColor(piece2);
  }

  hasAlliedPiece(piece1, piece2) {
    return this.hasPiece(piece2) && this.getColor(piece1) === this.getColor(piece2);
  }

  newMove(from, to) {
    let positions = JSON.parse(JSON.stringify(this.state.positions));
    let old_position = Array.from(positions[positions.length - 1]);
    let new_position = Array.from(old_position);
    if (this.getColor(old_position[from]) !== this.state.colorToMove) return;

    if (this.hasPiece(old_position[to])) {
      if (this.getColor(old_position[from]) === this.getColor(old_position[to])) return;
    }

    let moved_piece = old_position[from];
    new_position[to] = new_position[from];
    new_position[from] = 0;
    positions.push(new_position);
    this.setState(prevState => ({
      positions: positions,
      lastMove: [prevState.currentMove, moved_piece, to],
      currentMove: prevState.currentMove + 1,
      selectedMove: prevState.currentMove + 1,
      colorToMove: this.switchColor(),
      evalObj: this.evalPositionObj(new_position)
    }));
  }

}

export default App;

