      case 1: { // PAWN-white
        if(piece===1){
          if(row===6 && !this.hasPiece(position[square-16])) moves.push(square-16); // 2 forward
          if(!this.hasPiece(position[square-8])) moves.push(square-8); // 1 forward 

          if(column>=1 && row >=1){ 
            defends.push(this.convertBack(row-1, column-1));
            if(this.hasOpposingPiece(piece, position[this.convertBack(row-1, column-1)])) moves.push(this.convertBack(row-1, column-1)); // up-left-capture 
          }
          if(column<=6 && row >=1){
            defends.push(this.convertBack(row-1, column+1));
            if(this.hasOpposingPiece(piece, position[this.convertBack(row-1, column+1)])) moves.push(this.convertBack(row-1, column+1)); // up-right-capture 
          }
        }else{ // PAWN-black 
          if(row===1 && !this.hasPiece(position[square+16])) moves.push(square+16); // 2 forward
          if(!this.hasPiece(position[square+8])) moves.push(square+8); // 1 forward 

