  getRatio(w, b){
    if(w===0&&b===0) return 0;  
    let sum = w + b; 
    let whiteshare = w/sum*100; 
    // let blackshare = b/sum*100; 

    // this._square.style.setProperty("--separator", whiteshare+'%');
    return whiteshare; 
  }


  loadPGN(pgn){
    let r = chess.load_pgn(pgn);
    if(r){
      let history = chess.history(); 
      chess.reset();
      let fen_history = [chess.fen()];
      history.forEach(e=>{
        chess.move(e);
        fen_history.push(chess.fen());
      });
