  dropHandler = (e, target) => {
    e.preventDefault();
    this.removeClassesFromAll('dragover', 'moveable'); 
    let origin = e.dataTransfer.getData('text/plain');
    if(this.newMove(origin, target)){
      this.removeClassesFromAll('moveable', 'active');
      this.showBoardControl();
    }
