import React, {Component} from 'react';  
import './PopUp.css';  
import GamesList from '../GamesList/GamesList';

class Popup extends Component {
	constructor(props){
		super(props);
		this.state = {
			input: ''
		}
	}


	render(){ 
		let props = this.props.props;
		return (  
			<div className='popup'>  
				<div className='popup_inner'>
					<span id="closepopup" className='pointer' value="Close Pop-up" onClick={()=>props.closePopup(false)}>X</span>  
					{
						//props.content
					}
					{props.text ? <h1>{props.text}</h1> : ''} 
					{props.games ? <GamesList games={props.games} closePopup={props.closePopup} /> 
					: (<div><textarea 
						placeholder="Enter something" 
						className={props.textarea_class} 
						onChange={(e)=>this.changeHandler(e)} 
						autoFocus
						placeholder="https://lichess.org/game/export/6dFspuKY" 
						value={this.state.input}
						id="inputpgn" /> 
						<span 
						id="popupsubmit" 
						className='pointer' 
						value="submit" 
						onClick={()=>props.closePopup(document.getElementById('inputpgn').value)}
						>Submit
					</span></div>)}

				</div>  
			</div>  
		);  
	}

	changeHandler = (e) => {
		this.setState({input: e.target.value});
	}
}
export default Popup;
