import React, {useEffect} from 'react'; 
import './Moves.css';
import ImageButton from './../ImageButton/ImageButton';
import MoveButton from './MoveButton/MoveButton';

// let typeConversion = ['','B','N','R','Q','K'];
// let moveConverter = ["a8", "b8", "c8", "d8", "e8", "f8", "g8", "h8", "a7", "b7", "c7", "d7", "e7", "f7", "g7", "h7", "a6", "b6", "c6", "d6", "e6", "f6", "g6", "h6", "a5", "b5", "c5", "d5", "e5", "f5", "g5", "h5", "a4", "b4", "c4", "d4", "e4", "f4", "g4", "h4", "a3", "b3", "c3", "d3", "e3", "f3", "g3", "h3", "a2", "b2", "c2", "d2", "e2", "f2", "g2", "h2", "a1", "b1", "c1", "d1", "e1", "f1", "g1", "h1"];
// TYPES 
// PAWN - 1
// BISHOP - 2 
// KNIGHT - 3
// ROOK - 4 
// QUEEN - 5 
// KING - 6
const Moves = ({moves, onClick, selected_move, colorToMove}) => {

	// let colorToText = (color) => {
	// 	return color===1 ? 'white' : 'black'; 
	// }

	function moveButtonClicked(i){
		onClick(i);
	}

	useEffect(() => {
		if(selected_move===0) return;
		console.log('Moves, useEffect', selected_move);
		let moves_table = document.querySelector('#moves_table'); 
		let move_offset = Math.floor((selected_move-1)/2)*20; 
		console.log('Moves_table height', moves_table.clientHeight);
		console.log('Moves_table scrollTop', moves_table.scrollTop);
		console.log('selected_move offset', Math.floor((selected_move)/2)*20);

		if(moves_table.clientHeight+moves_table.scrollTop <= move_offset){
			moves_table.scrollTop = (move_offset-20) - moves_table.clientHeight;
		}
	});

	function moveClicked(i, e){
		// document.getElementById('moves_table').scrollTop = 0; 
		// document.getElementById('moves_table').scrollTop = e.target.offsetTop;
		onClick(i);
	}
	let render = []; 
	if(moves && moves.length>0){
		// let classes = ['move_number', 'move_white', 'move_black']; 
		let classes = ['move_white','move_black']; 
		let tick = 0;
		let count = 1;
		let buffer = [];

		for (let i = 0; i < moves.length; i++) {

			let buffer = []; 
			buffer.push(
				React.createElement('div', {className:'move_number', key:'mr'+count}, count)
				);
			buffer.push(
				React.createElement(
					'div', 
					{
						className:(i===selected_move-1)? classes[i%2]+' selected':classes[i%2], 
						id:'m'+i, 
						key:'m'+i, 
						onClick: (e)=>moveClicked(i, e)
					}, moves[i])
				);
			if(i+1<moves.length){
				i+=1; 
				buffer.push(
					React.createElement(
						'div', 
						{
							className:(i===selected_move-1)? classes[i%2]+' selected':classes[i%2], 
							id:'m'+i, 
							key:'m'+i, 
							onClick: (e) => moveClicked(i+1, e)
						}, moves[i])
				);
			}

			render.push(
				React.createElement('div', {className:'move_item', key:'mi'+count}, buffer)
				);
			count++; 

		}

	}
	return (
		<div id="moves"> 
			<div id="moves-container">
				<div id="top_row">
					<div className="left">Moves</div>
					<div className="right">
						<span className={'color '+colorToMove} /> to move
					</div>
				</div>

				<div id="table-container">
					<div className="move-button-container">
						<MoveButton onClick={()=>moveButtonClicked(0)} altText="Go to first Move" button="first_move" />
						<MoveButton onClick={()=>moveButtonClicked(selected_move-1)} altText="Go to previous Move" button="prev_move" />
						<MoveButton onClick={()=>moveButtonClicked(selected_move+1)} altText="Go to next Move" button="next_move" />
						<MoveButton onClick={()=>moveButtonClicked(999)} altText="Go to last Move" button="last_move" />
					</div>
						<div id="moves_table"> 	
							{render
							}
						</div>
				</div>
			</div>
		</div>
	)
}

export default Moves;
