import React from 'react'; 
import './Squares.css';
import Piece from '../Piece/Piece';

let squaresID= ["a8", "a7", "a6", "a5", "a4", "a3", "a2", "a1", "b8", "b7", "b6", "b5", "b4", "b3", "b2", "b1", "c8", "c7", "c6", "c5", "c4", "c3", "c2", "c1", "d8", "d7", "d6", "d5", "d4", "d3", "d2", "d1", "e8", "e7", "e6", "e5", "e4", "e3", "e2", "e1", "f8", "f7", "f6", "f5", "f4", "f3", "f2", "f1", "g8", "g7", "g6", "g5", "g4", "g3", "g2", "g1", "h8", "h7", "h6", "h5", "h4", "h3", "h2", "h1"]

class Squares extends React.Component{
	constructor(props){
		super(props);
		this.state = {
			piece_clicked: false
		}
	}

	render(){
		let {squares, onClick, pClick, onDrop, dragStart, dragEnd, colorToMove} = this.props;
		return (
			<div className='board'> 
				{
					squares.map((square, i)=>{
						return square 
						? <div 
							onDragOver={this.dragOverHandler} 
							onDragLeave={this.dragLeaveHandler} 
							onDrop={(e)=>onDrop(e, i)} 
							id={squaresID[i]}
							key={'square'+i} 
							className='square'> 
								<Piece 
									position={i}
									dragStart={dragStart} dragEnd={dragEnd}
									onClick={(e)=>this.pieceClick(e, pClick, i)} 
									piece={square.type}
									color={square.color} 
								/> 
						</div>
						: <div 
							id={'s'+i}
							onDragOver={this.dragOverHandler} 
							onDragLeave={this.dragLeaveHandler} 
							onClick={()=>this.squareClick(i, onClick)} 
							onDrop={(e)=>onDrop(e, i)} 
							key={'square'+i} 
							className='square' 

						/>;
					})
				}
			</div> 
		);
	}

	getPieceColor(piece){
		return piece<0 ? -1 : 1; 
	}

	pieceClick(e, callback, i){
		callback(i, e);
	}

	squareClick(square, onClick){
		if(!!(this.state.piece_clicked)){
			this.state.piece_clicked.parentElement.classList.remove('active'); 
			this.setState({piece_clicked: false});
		}
		onClick(square);
	}

	dragOverHandler = (e) => {
		e.preventDefault(e);
		requestAnimationFrame(function () {
			e.target.classList.add('dragover');
		});
	}

	dragLeaveHandler = (e) => {
		e.preventDefault(); 
		requestAnimationFrame(function () {
			e.target.classList.remove('dragover');
		}); 
	}
}
export default Squares;
