  //   history_copy[currentMove+1][from] = null;  
  //   history_copy[currentMove+1][to] = [type, squares_copy[to].color()]; 
  //   // let newposition = [...history_copy[currentMove]]; 
  //   // newposition[from] = null; 
  //   // newposition[to] = [type, squares_copy[to].color()];
  //   // history_copy[currentMove+1] = [...newposition];

  //   squares_copy[to].piece = squares_copy[from].piece;
  //   squares_copy[from].piece = null; 

