import React from 'react'; 
import './Square.css';
import Piece from '../Piece/Piece';
// import Draggable from 'react-draggable';

class Square extends React.Component{
	constructor(props){
		super(props); 
		const {row, column, background, piece, onClick, pieceClick} = props; 
		this.state = {
			row: row,
			column: column,
			background: background,
			piece: piece, 
			onClick: onClick, 
			pieceClick: pieceClick,
			dragover: ''
		}
	}

	handleClick = (e) => {
		// this.setState({background: 'white'}); 
		console.log(`square ${e} clicked`);
	};

	handlePieceClick = (e) => {
		console.log(`piece ${e} clicked`);
	};

	dropHandler = (e) => {
		e.preventDefault();
		console.log(e);
	};

	dragStartHandler = (e) => {
		// e.preventDefault();
		console.log(e.target);
		e.dataTransfer.dropEffect = "move";
		let element = e.target;

		requestAnimationFrame(function () {
			element.classList.add('hide');
		});
		 // e.dataTransfer.dropEffect = "move";
		 // e.dataTransfer.setData("text/plain", e.target.id+"/"+e.target.dataset.position); //e.target.id = ID von piece (pa1 usw.), position = current posistion 
		 // console.log(e);
	};

	dragOverHandler = (e) => {
		e.preventDefault(e);
		requestAnimationFrame(function () {
			e.target.classList.add('dragover');
		});
		// this.setState({dragover: 'dragover'}); 
	};

	dragLeaveHandler = (e) => {
		e.preventDefault(); 
		console.log('dragleave', e);
		requestAnimationFrame(function () {
			e.target.classList.remove('dragover');
		}); 
	};

	dragEndHandler = (e) =>{
		e.preventDefault();
		let element = e.target;
  	  	requestAnimationFrame(function () {
	    	element.classList.remove('hide');
	  	});

	};

	// dropHandler = (e) => {
	// 	e.preventDefault();
	// 	let data = e.dataTransfer.getData("text/plain");
 // 		let strs = data.split("/"); 
	// 	let piece = strs[0];
	// 	let original_position = strs[1];
	// 	let new_position = e.target.id;
	// 	if(e.target.localName=="piece"){
	// 		if(piece==e.target.id){
	// 	 		console.log("Same piece");
	// 	 		return; 
	// 	 	}else{
	// 	 		// console.log(piecesObj[original_position]);
	// 	 		// console.log(piecesObj[convertPieceID(e.target.id)]);
	// 	 		if(isOfSameColor(piecesObj[convertPieceID(piece)], piecesObj[convertPieceID(e.target.id)])){
	// 	 			console.log("Same color");
	// 	 			return;
	// 	 		}else{
	// 	 			capturePiece(original_position, e.target.parentNode.id);
	// 	 			return;
	// 	 		} 
	// 	 	}
	// 	}
	// 	else movePiece(original_position, new_position);
	// 	// console.log(e.target.id);
	// }

	render(){
		let {row, column, background, piece, onClick, pieceClick, dragover} = this.state; 
		let classes = 'square '+background;
		let position = [column, row];

		let returnThis = piece 
			? 
			<div className={classes+dragover} data-row={row} 
				// onClick={onClick} 
				data-column={column}
				onDragOver={this.dragOverHandler}
				onDragLeave={this.dragLeaveHandler}
				onDrop={this.dropHandler}
				> 
				<Piece position={position} 
					// onClick={pieceClick} 	
					onClick={() => this.handlePieceClick(position)}
					onDragStart={this.dragStartHandler}
					onDragEnd={this.dragEndHandler}
					type={piece[0]} color={piece[1]} /> 
			</div>
			: <div className={classes+dragover} onDrop={this.dropHandler} onDragLeave={this.dragLeaveHandler} onDragEnd={this.dragEndHandler} onDragOver={this.dragOverHandler} data-row={row} onClick={()=>this.handleClick('hi')} data-column={column} />;
		return returnThis;
	}

}

export default Square;
