import React, {Component} from 'react';  
import './PopUp.css';  

class Popup extends Component {
	constructor(props){
		super(props);
		this.state = {
			pgn_input: ''
		}
	}

	render(){ 
		let props = this.props;
		return (  
			<div className='popup'>  
				<div className='popup_inner'>
					<a href='#' onClick={()=>props.closePopup(false)}>X</a>  
					<h1>{props.text}</h1> 
					{/*<input type="text" placeholder="Enter PGN" onChange={this.changeHandler} id="inputpgn" /> */}
					<textarea placeholder="Enter PGN" onChange={this.changeHandler} id="inputpgn" /> 
					<button onClick={()=>props.closePopup(this.state.pgn_input)}>Submit</button>  
				</div>  
			</div>  
		);  
	}

	changeHandler = (e) =>{
		this.setState({pgn_input: e.target.value});
	}
}
export default Popup;
