import './App.css';
import React, { Component } from 'react';
import Moves from './components/Moves/Moves';
// import Squares from './components/Squares/Squares';
import Draggable from 'react-draggable';
import * as ChessJS from 'chess.js';
const Chess = typeof ChessJS === "function" ? ChessJS : ChessJS.Chess;
const chess = new Chess();

const squareID = ["a8", "b8", "c8", "d8", "e8", "f8", "g8", "h8", "a7", "b7", "c7", "d7", "e7", "f7", "g7", "h7", "a6", "b6", "c6", "d6", "e6", "f6", "g6", "h6", "a5", "b5", "c5", "d5", "e5", "f5", "g5", "h5", "a4", "b4", "c4", "d4", "e4", "f4", "g4", "h4", "a3", "b3", "c3", "d3", "e3", "f3", "g3", "h3", "a2", "b2", "c2", "d2", "e2", "f2", "g2", "h2", "a1", "b1", "c1", "d1", "e1", "f1", "g1", "h1"];
// console.log(chess.board().flat().reverse());

class App extends Component {
  constructor(){
    super();
    this.state = {
      colorToMove: chess.turn(),
      piece_clicked: false,
      moves: [],
      history: [],
      flipped: false
    };  
  }

  colorConv = color => {
    return color==='w' ? 'white' : 'black';
  }

  render(){
    let arr = this.state.flipped ? chess.board.flat().reverse() : chess.board().flat();
    let squares_ids = this.state.flipped ? squareID.reverse() : squareID;
    return(
      <div className='container'>
        <div className='board'> 
          {
            arr.map((square, i)=>{
              return square 
              ? 
              <div 
                onDragOver={this.dragOverHandler} 
                onDragLeave={this.dragLeaveHandler} 
                onDrop={(e)=>this.dropHandler(e, squareID[i])} 
                id={squares_ids[i]}
                key={'square'+i} 
                className='square'> 
                  <div 
                    className={`piece ${this.colorConv(square.color)} ${square.type}`}
                    draggable='true'
                    data-position={squares_ids[i]}
                    onDragStart={(e)=>this.dragStartHandler(e, squares_ids[i])} onDragEnd={()=>this.dragEndHandler}
                    onClick={()=>this.handlePieceClick(squares_ids[i])} 
                  />
              </div>
              : 
              <div 
                id={squares_ids[i]}
                onDragOver={this.dragOverHandler} 
                onDragLeave={this.dragLeaveHandler} 
                onClick={()=>this.handleClick(squares_ids[i])} 
                onDrop={(e)=>this.dropHandler(e, squares_ids[i])} 
                key={'square'+i} 
                className='square' 
            />;
          })
        }
      </div>
      <div className='moves'> 
        <Moves 
          onClick={this.moveClick} 
          moves={this.state.moves}
          colorToMove={this.state.colorToMove}
          evalClick={this.handleEvalClick}
          flip={this.flipClick}
          /> 
      </div>
      </div> 
    );
  } 

  dragOverHandler = (e) => {
    e.preventDefault(e);
    requestAnimationFrame(function () {
      e.target.classList.add('dragover');
    });
  }

  dragLeaveHandler = (e) => {
    e.preventDefault(); 
    requestAnimationFrame(function () {
      e.target.classList.remove('dragover');
    }); 
  }

  handleEvalClick = () => {
    console.log(this.state.history);
  }

  flipClick = () =>{

  }

  // @e: dragEvent
  // @i: square of dragged piece 
  dragStartHandler = (e, i) => {
    e.dataTransfer.effectAllowed = "all"
    e.dataTransfer.dropEffect = "move";
    e.dataTransfer.setData("text/plain", i);
    requestAnimationFrame(function () {
      e.target.classList.add('hide');
    });
    this.highlightMoves(i);   
  }

  dragEndHandler = (e) =>{
    e.preventDefault();
    requestAnimationFrame(function () {
        e.target.classList.remove('hide');
      });
  }

  dropHandler = (e, target) => {
    e.preventDefault();
    this.removeClassFromAll('dragover'); 
    this.removeClassFromAll('moveable');
    let origin = e.dataTransfer.getData('text/plain');
    this.newMove(origin, target);
  }

  getColorToMoveByMoveNumber(number){
    return number%2===0 ? 1 : -1; 
  }

  moveClick = (number) => {
    console.log('move clicked',number);
    chess.load(this.state.history[number]);
    this.setState({colorToMove: chess.turn()});
  }

  handleClick = (square) => {
    let clicked = this.state.piece_clicked; 
    if(clicked){
      if(this.newMove(clicked, square)){
        this.removeClassFromAll('moveable');
        this.removeClassFromAll('active');
        this.setState({piece_clicked: false});
      }else{
        // this.removeClassFromAll('active'); 
        // this.setState({piece_clicked: false })
      }
    }
  }

  squareColorConv = color => {
    if(color==='light') return 'w'; 
    else if(color==='dark') return 'b';
  }

  squaresOfSameColor(one, two){
    return chess.get(one).color===chess.get(two).color;
  }

  highlightMoves(piece){
    chess.moves({square:piece, verbose:true}).forEach(move=>{
      document.getElementById(move.to).classList.add('moveable');
    });
  }

  handlePieceClick = (square) => {
    let clicked = this.state.piece_clicked;
    if(!clicked){
      if(this.pieceIsTurnColor(square)){
        document.getElementById(square).classList.add('active');
        this.highlightMoves(square);
        this.setState({piece_clicked: square});
      }
    }else{
      this.removeClassesFromAll(['active', 'moveable']);
      if(this.squaresOfSameColor(square, clicked)){ // Wenn selbfarbiges, anderes piece gedrÃ¼ckt 
          document.getElementById(square).classList.add('active');
          this.highlightMoves(square);
          this.setState({piece_clicked: square});
      }else{ // Capture 
        if(this.newMove(clicked, square)){
          this.removeClassesFromAll(['active', 'moveable']);
          this.setState({piece_clicked: null});
        }
      }
    }
  }

  pieceIsTurnColor(piece){
    return chess.get(piece).color === chess.turn();
  }

  newMove(from, to){
    let res = chess.move({from,to});
    if(res){
      let history = [...this.state.history]; 
      history.push(chess.fen());
      this.setState({moves: chess.history(), colorToMove:chess.turn(), history});
      return res.san; 
    }else return false; 
  }

  attemptMove(from, to){
    return chess.move({from, to});
  }

  removeClassFromAll = remove => {
    document.querySelectorAll('.'+remove).forEach(el=>el.classList.remove(remove));
  }

  removeClassesFromAll = classes => {
    classes.forEach(e => document.querySelectorAll(''+e).forEach(el=>el.classList.remove(e)));
  }

  switchColor(){ return this.state.colorToMove === 1 ? -1 : 1; }
}

export default App;
