import React, {Component} from 'react'; 
import './Moves.css';


let columnConversion =  {1:'a', 2:'b', 3:'c', 4:'d', 5:'e', 6:'f', 7:'g', 8:'h'};
let typeConversion = ['','B','N','R','Q','K'];

// TYPES 
// PAWN - 0 
// BISHOP - 1 
// KNIGHT - 2 
// ROOK - 3 
// QUEEN - 4 
// KING - 5

class Moves extends Component {


	convertMove(move){
		let str = ''+move; 
		let r = Number(str.charAt(0)); 
		let c = columnConversion[Number(str.charAt(1))];
		return ''+c+r;
	}

	constructor(props){
		super(props);
		let {lastMove, onClick} = props;  
		// this.newMoveBlack = this.newMoveBlack.bind(this);
		this.state = {
			 render: {},
			 moves_white: [],
			 moves_black: [],
			 moveDisplay: 1
		}
	}

	numberOfRows(length){
		return Math.ceil(length/2);
	}

	newMoveWhite(move_number, type, move){
		let row_number = React.createElement('div', {className:'move_number',key:`move_number${move_number}`}, move_number);
		let white = React.createElement('div', {className:'move_white', key: `move_white${move_number}`, onClick:()=>this.props.onClick},typeConversion[type]+this.convertMove(move));
		return [row_number, white];
	}
	newMoveBlack(move_number, type, move){
		return React.createElement('div', {className:'move_black', key: `move_black${move_number}`, onClick:()=>this.props.onClick},typeConversion[type]+this.convertMove(move));

	}


	static getDerivedStateFromProps(props, state) {
		let {lastMove} = props; 
		if(!lastMove||lastMove===undefined) return null;
		let [color, type, move] = lastMove;
		console.log(lastMove);

		let moves_white = state.moves_white; 
		debugger; 
		console.log(moves_white);
		let moves_black = state.moves_black;
		// let render = state.render; 
		console.log("derived");	

		if(color===1){ //wnite
			moves_white.push({type: type, move: move});
		}else if(color===-1){ //black 
			moves_black.push({type:type, move:move});
		}
		console.log(moves_white);
		// let moveDisplay = state.moveDisplay; 


		// if(move_number%2===0){
		// 	moveDisplay++;
		// 	moves.push(React.createElement('div', {className:'move_number',key:`move_number${move_number}`}, moveDisplay));
		// 	moves.push(React.createElement('div', {className:'move_white', key: `move_white${move_number}`},type+':'+this.convertMove(move)));
		// }else moves.push(React.createElement('div', {className:'move_black', key: `move_black${move_number}`},type+':'+this.convertMove(move))); 

		return {moves_white: moves_white, moves_black: moves_black}
	}		

	render(){
		let moves_white = this.state.moves_white;
		let moves_black = this.state.moves_black;
		let render = []; 
		let c = 1; 

		// console.log(moves_white);

		moves_white.forEach((element, index)=>{
			render.push(this.newMoveWhite(c, element.type, element.move));
			if(moves_black[index]) render.push(this.newMoveBlack(c, moves_black[index].type, moves_black[index].move));
		});



		return (
			<div id="moves"> 
				<div id="top_row"> Moves </div>
				<div id="moves_table"> 
					{render
					}
				</div>
			</div>
		)
	}
}

export default Moves;
