import React from 'react'; 
import './Squares.css';
import Piece from '../Piece/Piece';


class Squares extends React.Component{
	constructor(props){
		super(props);
		this.state = {
			position: this.props.position
		}
	}

	render(){
		let position = this.state.position;
		let {onClick, pClick, onDrop} = this.props;
		return (
			<div className='board'> 
				{
					position.map((element, i)=>{
						return element ===0 ? <div onDragOver={this.dragOverHandler} onDragLeave={this.dragLeaveHandler} onClick={()=>this.squareClick(i, onClick)} onDrop={this.dropHandler} key={'square'+i} className='square' />
						:
							<div onDragOver={this.dragOverHandler} onDragLeave={this.dragLeaveHandler} onClick={()=>this.squareClick(i, onClick)} onDrop={(e)=>onDrop(e, i)} key={'square'+i} className='square'> 
								<Piece position={i} onClick={pClick} piece={element} dragStart={this.dragStartHandler} /> 
							</div>; 
					})
				}
			</div> 
		);
	}

	dropHandler(e){
		e.preventDefault();
		console.log(e.dataTransfer);
	}

	squareClick(square, onClick){
		onClick(square);
	}

	dragOverHandler = (e) => {
		e.preventDefault(e);
		requestAnimationFrame(function () {
			e.target.classList.add('dragover');
		});
	}
	dragLeaveHandler = (e) => {
		e.preventDefault(); 
		requestAnimationFrame(function () {
			e.target.classList.remove('dragover');
		}); 
	}

	dragStartHandler(e){
		console.log('hi');
		e.dataTransfer.dropEffect = "move";
		e.dataTransfer.setData("text/plain", '55'); 
		// requestAnimationFrame(function () {
		// 	e.target.classList.add('hide');
		// });
	}

	dragEndHandler = (e) =>{
		e.preventDefault();
	// let element = e.target;
	//   	requestAnimationFrame(function () {
 //    	element.classList.remove('hide');
 //  	});
	}
}
export default Squares;
