import React from 'react'; 
import './Squares.css';
import Piece from '../Piece/Piece';


class Squares extends React.Component{
	constructor(props){
		super(props);
		this.state = {
			position: this.props.position
		}
	}

	render(){
		let position = this.state.position;
		let {onClick, pClick, onDrop} = this.props;
		return (
			<div className='board'> 
				{
					position.map((element, i)=>{
						if(element===0) return <div onDragOver={this.dragOverHandler} onDragStart={this.dragStartHandler} onDragLeave={this.dragLeaveHandler} onDragEnd={this.dragEndHandler} onClick={()=>this.squareClick(i, onClick)} onDrop={onDrop} key={'square'+i} className='square' />;
						else return <div onDragOver={this.dragOverHandler} onDragStart={this.dragStartHandler} onDragLeave={this.dragLeaveHandler} onDragEnd={this.dragEndHandler} onClick={()=>this.squareClick(i, onClick)} onDrop={onDrop} key={'square'+i} className='square'> <Piece onClick={pClick} piece={element} /> </div>; 
					})
				}
			</div> 
		);
	}

	squareClick(square, onClick){
		onClick(square);
	}

	dragOverHandler = (e) => {
		e.preventDefault(e);
		requestAnimationFrame(function () {
			e.target.classList.add('dragover');
		});
	}

	dragStartHandler = (e) => {
		e.dataTransfer.dropEffect = "move";
		e.dataTransfer.setData("text/plain", e.target.dataset.position); 
		requestAnimationFrame(function () {
			e.target.classList.add('hide');
		});
	}

	dragLeaveHandler = (e) => {
		e.preventDefault(); 
		requestAnimationFrame(function () {
			e.target.classList.remove('dragover');
		}); 
	}

	dragEndHandler = (e) =>{
		e.preventDefault();
		let element = e.target;
		  	requestAnimationFrame(function () {
	    	element.classList.remove('hide');
	  	});
	}
}
export default Squares;
