import React, {Component} from 'react'; 
import './Moves.css';


let columnConversion =  {1:'a', 2:'b', 3:'c', 4:'d', 5:'e', 6:'f', 7:'g', 8:'h'};	

class Moves extends Component {


	convertMove(move){
		let str = ''+move; 
		let r = Number(str.charAt(0)); 
		let c = columnConversion[Number(str.charAt(1))];
		return ''+c+r;
	}

	constructor(props){
		super(props);
		let {lastMove} = props;  
		// this.newMoveBlack = this.newMoveBlack.bind(this);
		this.state = {
			 moves: [],
			 history: [],
			 playedMoves: 0,
			 moveDisplay: 1
		}
	}

	numberOfRows(length){
		return Math.ceil(length/2);
	}

	newMoveWhite(move_number, type, move){
		let row_number = React.createElement('div', {className:'move_number',key:`move_number${move_number}`}, move_number);
		let white = React.createElement('div', {className:'move_white', key: `move_white${move_number}`},type+':'+this.convertMove(move));
		return [row_number, white];
	}
	newMoveBlack(move_number, type, move){
		let black = React.createElement('div', {className:'move_black', key: `move_black${move_number}`},type+':'+this.convertMove(move));
		return black;
	}


	static getDerivedStateFromProps(props, state) {
		let {lastMove} = props; 
		if(!lastMove||lastMove===undefined) return null;
		let [move_number, type, move] = lastMove;
		let moveDisplay = state.moveDisplay; 
		let moves = state.moves; 

		if(move_number%2===0){
			moveDisplay++;
			moves.push(this.newMoveWhite(moveDisplay, type, move));
		}else moves.push(this.newMoveBlack(moveDisplay, type, move_number)); 

		return {moves: moves, moveDisplay: moveDisplay}
	}		

	render(){
		// let history = this.state.history;

		// let render = [];
		// let move_number = 1; 
		// console.log('history', history);
		// history.forEach((element,index)=>{
		// 	console.log('element0', element[0]);
		// 	console.log('element1', element[1]);
		// 	if(index%2===0){
		// 		render.push(this.newMoveWhite(element[1], element[0], move_number));
		// 		move_number++;
		// 	}else render.push(this.newMoveBlack(element[1], element[0], move_number)); 
		// });
		console.log(this.state.moves);

		return (
			<div id="moves"> 
				<div id="top_row"> Moves </div>
				<div id="moves_table"> 
					{//render
					}
				</div>
			</div>
		)
	}
}

export default Moves;
