import React, { Component } from 'react';
import './Board.css'; 
import Square from '../Square/Square';


class Board extends Component {
	constructor(){
		super();
	}

	initSquares(){
		let squares = [];
		let r = 8;
		let c = 1; 
		let bgs = ['black', 'white'];
		let tick = 0; 
		for(let i =0;i<64;i++){
			squares.push(<Square key={''+r+c} row={r} column={c} background={bgs[tick]}  />);
			c++; 
			if(c>8){
				c = 1; 
				r--;
			}else{
				tick = tick === 0 ? 1 : 0;
			}
		}
		return squares; 
	}

	render(){
		return (
			<div id="board"> 
				{this.initSquares()}
			</div>
		);
	}
}
export default Board;
