import React, { Component } from 'react';
import './Board.css'; 
import Square from '../Square/Square';


class Board extends Component {
	constructor(){
		super();
	}


	renderSquare(){
	    return (
	      <Square />
	    );  
	}
	initSquares(){
		let squares = [];
		let r = 7;
		let c = 0; 
		for(let i =0;i<63;i++){
			squares.push(<Square row={r} column={c} />);
			c++; 
			if(c>7){
				c = 0; 
				r--;
			}
		}
		return squares; 
	}

	render(){
		return (
			<div id="board"> 
				{this.initSquares()}
			</div>
		);
	}
}
export default Board;
