import './App.css';
import React, { Component } from 'react';
import Moves from './components/Moves/Moves';
import PopUp from './components/PopUp/PopUp';
import Button from './components/Button/Button';
// import Squares from './components/Squares/Squares';
import Draggable from 'react-draggable';
import * as ChessJS from 'chess.js';
const Chess = typeof ChessJS === "function" ? ChessJS : ChessJS.Chess;
const chess = new Chess();

const squareID = ["a8", "b8", "c8", "d8", "e8", "f8", "g8", "h8", "a7", "b7", "c7", "d7", "e7", "f7", "g7", "h7", "a6", "b6", "c6", "d6", "e6", "f6", "g6", "h6", "a5", "b5", "c5", "d5", "e5", "f5", "g5", "h5", "a4", "b4", "c4", "d4", "e4", "f4", "g4", "h4", "a3", "b3", "c3", "d3", "e3", "f3", "g3", "h3", "a2", "b2", "c2", "d2", "e2", "f2", "g2", "h2", "a1", "b1", "c1", "d1", "e1", "f1", "g1", "h1"];


// console.log(chess.board().flat().reverse());

class App extends Component {
  constructor(){
    super();
    this.state = {
      colorToMove: chess.turn(),
      selected_move: 0,
      piece_clicked: false,
      // board: chess.board().flat(),
      history: [], // single moves 
      fen_history: [chess.fen()], // moves in fen 
      flipped: false,
      loaded_game: null,
      popUp: false, 
      control: false,
      entered_pgn: '',
      white_moves: null,
      black_moves: null
    };  
  }

  componentDidMount(){
    document.addEventListener("keydown", this.keyDownHandler);
  }

  colorConv = color => {
    return color==='w' ? 'white' : 'black';
  }

  initSquares(){
    let squares = {}; 
    squareID.forEach(e=>{
      squares[e] = {'w':0,'b':0};
    });
    return squares;
  }


  render(){
    // let arr = this.state.flipped ? chess.board().flat().reverse() : chess.board().flat();
    chess.load(this.state.fen_history[this.state.selected_move]);
    let arr = chess.board().flat();
    let squares_ids = this.state.flipped ? squareID.reverse() : squareID;
    return(
      <div className='container'>
        <div className='board'> 
          {
            arr.map((square, i)=>{
              return square 
              ? 
              <div 
                onDragOver={this.dragOverHandler} 
                onDragLeave={this.dragLeaveHandler} 
                onDrop={(e)=>this.dropHandler(e, squareID[i])} 
                id={squares_ids[i]}
                key={'square'+i} 
                className='square'> 
                  <div 
                    className={`piece ${this.colorConv(square.color)} ${square.type}`}
                    draggable='true'
                    data-position={squares_ids[i]}
                    onDragStart={(e)=>this.dragStartHandler(e, squares_ids[i])} onDragEnd={()=>this.dragEndHandler}
                    onClick={()=>this.handlePieceClick(squares_ids[i])} 
                  />
              </div>
              : 
              <div 
                id={squares_ids[i]}
                onDragOver={this.dragOverHandler} 
                onDragLeave={this.dragLeaveHandler} 
                onClick={()=>this.handleClick(squares_ids[i])} 
                onDrop={(e)=>this.dropHandler(e, squares_ids[i])} 
                key={'square'+i} 
                className='square' 
            />;
          })
        }
        </div>
        <Moves 
          onClick={this.moveClick} 
          selected_move={this.state.selected_move}
          moves={this.state.history}
          colorToMove={this.state.colorToMove}
          evalClick={this.handleEvalClick}
          flip={this.flipClick}
          control={this.handleControlClick}
          /> 
        <div className="button_wrapper"> 
          <Button text='Random Position' onClick={this.handleEvalClick} />
          <Button text='Enter PGN' onClick={this.pgnClick} />
          <Button text='Evaluate' onClick={this.handleControlClick} />
        </div>
        {this.state.popUp ?  
          <PopUp  
            text='Enter PGN'  
            closePopup={this.closePopup}  
          />  
          : null  
        }  
      </div> 
    );
  } 

  closePopup = (text) => {
    console.log("hi", text);
    if(!text) this.setState({popUp: false});
    else{
      this.setState({popUp: false, entered_pgn: text}); 
      this.loadPGN(text);
    }
  }

  diagonalPawnMoves(square, color){
    if(this.isOnFirstOrLastRank(square)) return false; 
    let moves = [];
    let [l, r] = color==='w' ? [-9, -7] : [7, 9];
    switch(this.isOnEdge(square)){
      case 0:
        moves.push(squareID[square+l], squareID[square+r]);
        break;
      case 1: 
        moves.push(squareID[square+r]);
        break;
      case 2: 
        moves.push(squareID[square+l]);
        break;
      default: moves.push(squareID[square+l], squareID[square+r]);
        break; 
    }
    return moves;
  }

  isOnFirstOrLastRank(square){
    return (square>=0&&square<=7)||(square>=56&&square<=63);
  }

  /*
  return 1 ... left Edge, 
         2 ... right edge 
         0 ... not on edge
  */
  isOnEdge(square){
    if([0,8,16,24,32,40,48,56].includes(square)) return 1;
    if([7,15,23,31,39,47,55,63].includes(square)) return 2; 
    return 0; 
  }

  keyDownHandler = (e)=>{
    switch(e.keyCode){
      case 39: 
        e.preventDefault();
        if(this.state.selected_move+1<=this.state.fen_history.length-1){ 
          this.setState(prevState=>({selected_move: prevState.selected_move+1}));                
          // this.removeClassFromAll('selected');
          // document.getElementById('m'+this.state.selected_move+1).classList.add('selected');
        }
        break;
      case 37: 
        e.preventDefault();
        if(this.state.selected_move>=1){
          this.setState(prevState=>({selected_move: prevState.selected_move-1}));
          // this.removeClassFromAll('selected');
          // document.getElementById('m'+this.state.selected_move-1).classList.add('selected');
        }
        break;
      case 27: 
        e.preventDefault(); 
        if(this.state.popUp){
          this.setState({popUp: false});
        }
        break;
      default: break; 
    }
  }

  dragOverHandler = (e) => {
    e.preventDefault(e);
    requestAnimationFrame(function () {
      e.target.classList.add('dragover');
    });
  }

  dragLeaveHandler = (e) => {
    e.preventDefault(); 
    requestAnimationFrame(function () {
      e.target.classList.remove('dragover');
    }); 
