import React from 'react'; 
import './Square.css';
import Piece from '../Piece/Piece';
// import Draggable from 'react-draggable';

const original_rank = {
	'0': 2, '-1': 7
}

const dragOverHandler = (e) => {
	e.preventDefault(e);
	requestAnimationFrame(function () {
		e.target.classList.add('dragover');
	});
};

const dragStartHandler = (e) => {
	e.dataTransfer.dropEffect = "move";
	e.dataTransfer.setData("text/plain", e.target.dataset.position); 
	requestAnimationFrame(function () {
		e.target.classList.add('hide');
	});
};

const dragLeaveHandler = (e) => {
	e.preventDefault(); 
	requestAnimationFrame(function () {
		e.target.classList.remove('dragover');
	}); 
};

const dragEndHandler = (e) =>{
	e.preventDefault();
	let element = e.target;
	  	requestAnimationFrame(function () {
    	element.classList.remove('hide');
  	});

};

function Square({index, onClick, onDrop, piece, pClick, classes}){
	let classnames = 'square '+classes;


	// let availableMoves = (type) => {
	// 	switch(type){
	// 		case 0: 
	// 			console.log('pawn');
	// 			break;

	// 		default: return;
	// 	}
	// }

	return piece===0
		? 
 		<div className={classnames} 
			onDrop={onDrop}
			data-position={index} 
			onDragLeave={dragLeaveHandler} 
			onDragEnd={dragEndHandler} 
			onDragOver={dragOverHandler} 
			onClick={() => onClick(index)}
			id={"s"+index} />
		: <div className={classnames} id={'s'+index}
			data-position={index}
			onDragOver={dragOverHandler}
			onDragLeave={dragLeaveHandler}
			onDrop={onDrop}
			> 
			<Piece position={index} 
				// onClick={() => availableMoves(piece[0])}
				onDragStart={dragStartHandler}
				onClick={pClick}
				onDragEnd={dragEndHandler}
				piece={piece} 
			/> 
		</div>		
}

export default Square;
