import React, { Component } from 'react';
import './Board.css'; 
import Square from '../Square/Square';


// TYPES 
// PAWN - 0 
// BISHOP - 1 
// KNIGHT - 2 
// ROOK - 3 
// QUEEN - 4 
// KING - 5
const startingPosition = 
{
	// position: [type, color]
	//PAWNS 
	21: [0, 1], 22: [0, 1], 23: [0, 1], 24: [0, 1], 25: [0, 1], 26: [0, 1], 27: [0, 1], 28: [0, 1],
	71: [0, -1], 72: [0, -1], 73: [0, -1], 74: [0, -1], 75: [0, -1], 76: [0, -1], 77: [0, -1], 78: [0, -1],

	81: [3, -1], 11: [3, 1], 88: [3, -1], 18: [3, 1], // ROOKS 
	82: [2, -1], 13: [2,1], 17:[2,1], 87: [2,-1], // KNIGHTS 
	83: [1,-1], 86: [1,-1], 12: [1,1], 16: [1,1], // BISHOPS 
	14: [4,1], 84: [4,-1], //QUEENS 
	15: [5,1], 85: [5,-1] //KINGS 
};
const boardLayout = 
[81,82,83,84,85,86,87,88,
71,72,73,74,75,76,77,78,
61,62,63,64,65,66,67,68,
51,52,53,54,55,56,57,58,
41,42,43,44,45,46,47,48,
31,32,33,34,35,36,37,38,
21,22,23,24,25,26,27,28,
11,12,13,14,15,16,17,18
];
// const boardLayout = 
// [
// 11,12,13,14,15,16,17,18,
// 21,22,23,24,25,26,27,28,
// 31,32,33,34,35,36,37,38,
// 41,42,43,44,45,46,47,48,
// 51,52,53,54,55,56,57,58,
// 61,62,63,64,65,66,67,68,
// 71,72,73,74,75,76,77,78,
// 81,82,83,84,85,86,87,88
// ];


class Board extends Component {
	constructor(props){
		super(props);
		this.state = {
			squares: this.initSquares(),
			handleClick: this.handleClick,
			handlePieceClick: this.handlePieceClick,
			render: []
		};
	}

	handleClick = (e) => {
		console.log("square", e);
		// console.log(this.state.squares[e]);
	};

	handlePieceClick = (e) => {
		console.log("piece", e);
	};

	// movePiece = (start, end) => {
	// 	squares.start
	// };



	initSquares = () => {
		let squares = {};
		let r = 8;
		let c = 1; 
		let bgs = ['black', 'white'];
		let tick = 0; 
		for(let i =0;i<64;i++){
			let pos = Number(''+c+r);
			if(pos in startingPosition){
				squares[pos] = 
				{
					key: ''+c+r,
					row: r,
					column: c,
					background: bgs[tick],
					piece: startingPosition[pos] 
				};
				// (<Square key={''+c+r} 
				// 	pieceClick={3}
				// 	// pieceClick = {() => this.handlePieceClick(pos)} 
				// 	// onClick={() => this.handleClick(pos)} 
				// 	row={r} column={c} background={bgs[tick]} piece={startingPosition[pos]} />);
			} 
			else 
				squares[pos] = {
					key: ''+c+r,
					row: r,
					column: c,
					background: bgs[tick],
					piece: null 
				};
				// squares[pos] = (<Square key={''+c+r} onClick={() => this.handleClick(pos)} row={r} piece={null} column={c} background={bgs[tick]}  />);
			c++; 
			if(c>8){
				c = 1; 
				r--;
			}else tick = tick === 0 ? 1 : 0;

		}
		return squares; 
	};

	renderSquares = (squares) => {
		console.log();
		let render = [];

		boardLayout.forEach(element => {
			let curr = squares[element];
			render.push(<Square key={''+curr.column+curr.row} row={curr.row} column={curr.column} background={curr.background} piece={curr.piece} />);
		});

		// Object.keys(squares).forEach(key => {
		//   let curr = squares[key]; 
		//   // console.log(curr);
		//   render.push(<Square key={''+curr.column+curr.row} row={curr.row} column={curr.column} background={curr.background} piece={curr.piece} />);
		// });
		return render; 
	};


	// static getDerivedStateFromProps(props, state) {
 //    	return {favoritecolor: props.favcol };
 //  	}

 	componentDidMount() {

 	}

	render(){
		return (
			<div id="board"> 
				 { this.renderSquares(this.state.squares)
				 }
			</div>
		);
	}
}
export default Board;
