import React from 'react'; 
import './Squares.css';
import Piece from '../Piece/Piece';


class Squares extends React.Component{
	constructor(props){
		super(props);
		let {position, onClick, pClick, onDrop} = props;
		this.state = {
			position: position
		}
	}

	render(){
		let position = this.state.position; 
		return (
			<div className='board'> 
				{
					position.map((element, i)=>{
						if(element===0) return <div className='square'/>;
						else <div className='square'> return <Piece piece={element} /> </div>; 

					})
				}
			</div> 
		);
	}

	dragOverHandler = (e) => {
		e.preventDefault(e);
		requestAnimationFrame(function () {
			e.target.classList.add('dragover');
		});
	}

	dragStartHandler = (e) => {
		e.dataTransfer.dropEffect = "move";
		e.dataTransfer.setData("text/plain", e.target.dataset.position); 
		requestAnimationFrame(function () {
			e.target.classList.add('hide');
		});
	}

	dragLeaveHandler = (e) => {
		e.preventDefault(); 
		requestAnimationFrame(function () {
			e.target.classList.remove('dragover');
		}); 
	}

	dragEndHandler = (e) =>{
		e.preventDefault();
		let element = e.target;
		  	requestAnimationFrame(function () {
	    	element.classList.remove('hide');
	  	});
	}
}



