import React, { Component } from 'react';
import './Board.css'; 
import Square from '../Square/Square';



// TYPES 
// PAWN - 0 
// BISHOP - 1 
// KNIGHT - 2 
// ROOK - 3 
// QUEEN - 4 
// KING - 5 


const startingPosition = 
{
	//PAWNS 
	12: [0, 1], 22: [0, 1], 32: [0, 1], 42: [0, 1], 52: [0, 1], 62: [0, 1], 72: [0, 1], 82: [0, 1],
	17: [0, -1], 27: [0, -1], 37: [0, -1], 47: [0, -1], 57: [0, -1], 67: [0, -1], 77: [0, -1], 87: [0, -1],

	18: [3, -1], 11: [3, 1], 88: [3, -1], 81: [3, 1], // ROOKS 
	28: [2, -1], 31: [2,1], 71:[2,1], 78: [2,-1], // KNIGHTS 
	38: [1,-1], 68: [1,-1], 21: [1,1], 61: [1,1], // BISHOPS 
	41: [4,1], 48: [4,-1], //QUEENS 
	51: [5,1], 58: [5,-1] //KINGS 
};

class Board extends Component {
	constructor(props){
		super(props);
		this.state = {
			squares: Array(64).fill(null)
		};
	}

	handleClick = (e) => {
		console.log(e);
	};

	initSquares(){
		let squares = [];
		let r = 8;
		let c = 1; 
		let bgs = ['black', 'white'];
		let tick = 0; 
		for(let i =0;i<64;i++){
			let pos = Number(''+c+r);
			if(pos in startingPosition){
				squares.push(<Square key={''+c+r} onClick={() => this.handleClick(pos)} row={r} column={c} background={bgs[tick]} piece={startingPosition[pos]} />);
				// console.log("in!");	
			} 
			else squares.push(<Square key={''+c+r} onClick={() => this.handleClick(pos)} row={r} column={c} background={bgs[tick]}  />);
			c++; 
			if(c>8){
				c = 1; 
				r--;
			}else{
				tick = tick === 0 ? 1 : 0;
			}
		}
		return squares; 
	}

	render(){
		return (
			<div id="board"> 
				{this.initSquares()}
			</div>
		);
	}
}
export default Board;
