import React, {Component} from 'react'; 
import './Moves.css';


let columnConversion =  {1:'a', 2:'b', 3:'c', 4:'d', 5:'e', 6:'f', 7:'g', 8:'h'};	

class Moves extends Component {


	convertMove(move){
		let str = ''+move; 
		let r = Number(str.charAt(0)); 
		let c = columnConversion[Number(str.charAt(1))];
		return ''+c+r;
	}

	constructor(props){
		super(props);
		let {lastMove} = props;  // moveHistory: [[from, to], [from, to], [from, to], ...]
		// this.newMoveBlack = this.newMoveBlack.bind(this);
		this.state = {
			 render: [],
			 history: [],
			 playedMoves: 0
		}
	}

	numberOfRows(length){
		return Math.ceil(length/2);
	}

	newMoveWhite(move, move_number){
		let row_number = React.createElement('div', {className:'move_number',key:`move_number${move_number}`}, move_number);
		let white = React.createElement('div', {className:'move_white', key: `move_white${move_number}`},this.convertMove(move));
		return [row_number, white];
	}
	newMoveBlack(move, move_number){
		let black = React.createElement('div', {className:'move_black', key: `move_black${move_number}`},this.convertMove(move));
		return black;
	}


	static getDerivedStateFromProps(props, state) {
		let {lastMove} = props; 
		console.log('last move', lastMove);
		if(!lastMove||lastMove===undefined) return null;
		let history_copy = [...state.history];
		history_copy.push([lastMove]); 
		// this.setState(prevState=>({history:history_copy}));
		return {
			history: history_copy
		};
	}		

	render(){
		let history = this.state.history;

		let render = [];
		let move_number = 1; 
		console.log(history);

		// history.forEach((element,index)=>{
		// 	if(index%2===0){
		// 		render.push(this.newMoveWhite(element, move_number));
		// 		move_number++;
		// 	}else render.push(this.newMoveBlack(element, move_number)); 
		// });


		return (
			<div id="moves"> 
				<div id="top_row"> Moves </div>
				<div id="moves_table"> 
					{render}
				</div>
			</div>
		)
	}
}

export default Moves;
