import React from 'react'; 
import './Piece.css';
import Draggable from 'react-draggable';

const typeConv = {
	1: 'pawn', 2:'bishop', 3:'knight', 4:'rook', 5:'queen', 6:'king'
}

	// <Piece position={index} 
	// 			// onClick={() => availableMoves(piece[0])}
	// 			onDragStart={dragStartHandler}
	// 			onDragEnd={dragEndHandler}
	// 			piece={piece} 

let colorConv = number => {
	if(number>0) return 'white';
	else return 'black'; 
}

const dragStart= (e, i) => {
	e.dataTransfer.effectAllowed = "all"
	e.dataTransfer.dropEffect = "move";
	e.dataTransfer.setData("text/plain", i); 
	// requestAnimationFrame(function () {
	// 	e.target.classList.add('hide');
	// });
}
const dragEndHandler = (e) =>{
	e.preventDefault();
	requestAnimationFrame(function () {
    	e.target.classList.remove('hide');
  	});
}


const Piece = ({position, piece, onClick}) => {
	let pieceClass = `piece ${typeConv[Math.abs(piece)]} ${colorConv(piece)}`;
	// let key = `p${position}`;
	return <div className={pieceClass} onDragStart={(e) => dragStart(e, position)} onClick={() => onClick(position)} draggable="true" />;
}

export default Piece;
