import React, {useEffect, useState} from 'react'; 
import './GamesList.css';


const GamesList = ({closePopup}) => {

	let render = []; 
	let whiteplayer = /\[White\s"?(.*?)"?\]/; 
	let blackplayer = /\[Black\s"?(.*?)"?\]/; 
	let date = /\[Date\s"?(.*?)"?\]/; 

	const [games, setGames] = useState(''); 

	loadStudy = () => {
		fetch('http://localhost:3001/game?all=true')
	      	.then(res => res.json())
	     	.then(res => {
	        	this.renderGames(res);
        	});
	}

	useEffect(() => {
		this.loadStudy(); 
	}, []); 

	renderGames = (games) => {
		let render = []; 
		games.forEach(game => {
			render.push({
				white: game.match(whiteplayer)[1],
				black: game.match(blackplayer)[1],
				date: game.match(date)[1],

				// title: game.match(whiteplayer)[1] + ' vs. ' + game.match(blackplayer)[1] + ' ('+game.match(date)[1]+')',
				pgn: game
			});
		});
		this.setGames(render); 
	}

	return (
		<div className="games_container">
			{ 
				games.map((i, c) => {
					<div key={c+'game'} onClick={() => closePopup(i.pgn)} className="game">
						<div className="white">{i.white}</div>
						<div className="date">{i.date}</div>
						<div className="black">{i.black}</div>
					</div>
				})
			}
		</div>
	);
}
export default GamesList;
