import './App.css';
import React, { Component } from 'react';
// import Board from './components/Board/Board';
import Moves from './components/Moves/Moves';
import Square from './components/Square/Square';


// TYPES 
// PAWN - 0 
// BISHOP - 1 
// KNIGHT - 2 
// ROOK - 3 
// QUEEN - 4 
// KING - 5
const startingPosition = 
{
  // position: [type, color]
  //PAWNS 
  21: [0, 1], 22: [0, 1], 23: [0, 1], 24: [0, 1], 25: [0, 1], 26: [0, 1], 27: [0, 1], 28: [0, 1],
  71: [0, -1], 72: [0, -1], 73: [0, -1], 74: [0, -1], 75: [0, -1], 76: [0, -1], 77: [0, -1], 78: [0, -1],

  81: [3, -1], 11: [3, 1], 88: [3, -1], 18: [3, 1], // ROOKS 
  82: [2, -1], 13: [2,1], 17:[2,1], 87: [2,-1], // KNIGHTS 
  83: [1,-1], 86: [1,-1], 12: [1,1], 16: [1,1], // BISHOPS 
  14: [4,1], 84: [4,-1], //QUEENS 
  15: [5,1], 85: [5,-1] //KINGS 
};
const boardLayout = 
[81,82,83,84,85,86,87,88,
71,72,73,74,75,76,77,78,
61,62,63,64,65,66,67,68,
51,52,53,54,55,56,57,58,
41,42,43,44,45,46,47,48,
31,32,33,34,35,36,37,38,
21,22,23,24,25,26,27,28,
11,12,13,14,15,16,17,18
];

class App extends Component {
  constructor(){
    super();
    // this.initSquares=this.initSquares.bind(this);
    // this.renderSquares=this.renderSquares.bind(this);
    this.state = {
      // moveHistory: [],
      currentMove: 0,
      lastMove: null,
      colorToMove: 1,
      selectedMove: 0,
      squares: this.initSquares(0, startingPosition),
      history: [startingPosition],
    };  
  }

  initSquares = (currentMove, position) => {
    let squares = [];
    let click = this.handleClick;
    let pclick = this.handlePieceClick;
    let r = 8;
    let c = 1; 
    let bgs = ['black', 'white'];
    let tick = 0; 
    let onDrop = this.dropHandler;
    for(let i =0;i<64;i++){
      let pos = Number(''+c+r);
      squares[pos] = {
        key: ''+c+r,
        row: r,
        column: c,
        background: bgs[tick],
        piece: position[pos],
        output: function(){
          let p = this.piece ? this.piece : null; 
          return <Square key={currentMove+'/'+this.column+this.row} onClick={click} onDrop={onDrop} row={this.row} pieceClick={pclick} column={this.column} background={this.background} piece={p} />;
        },
        hasPiece: function(){
          return (this.piece===null||this.piece===undefined) ? false : true; 
        },
        color: function(){
          return this.hasPiece() ? this.piece[1] : false;  
        },
        type: function(){
          return this.hasPiece() ? this.piece[0] : false;  
        }
      };
      c++; 
      if(c>8){
        c = 1;
        r--;
      }else tick = tick === 0 ? 1 : 0;
    }
    return squares; 
  };

  renderSquares = (squares) => {
    let render = [];
    boardLayout.forEach(element => {
      render.push(squares[element].output());
    });
    return render; 
  };

  moveClick = (color, number) => {
    console.log('move clicked', color, number);
    this.setState(prevState=>({
      squares: this.initSquares(number, prevState.history[number])
    }));
  }

  handleClick = (e) => {
    console.log(this.state.squares_history);
    this.setState(prevState=>({
      squares: [...this.state.squares_history[0]]
    }));
  };

  handlePieceClick = (e, position) => {
    this.state.squares[position].availableMoves();
  }

  dropHandler = (e) => {
    e.preventDefault();
    e.target.classList.remove('dragover');
    let target = e.target.dataset.position; 
    let origin = e.dataTransfer.getData("text/plain");
    // console.log(`origin: ${origin}, target: ${target}`);
    this.newMove(origin, target)
  };

  switchColor(){
    return this.state.colorToMove === 1 ? -1 : 1; 
  }

  newMove(from, to){
    let squares_copy = [...this.state.squares];
    if(squares_copy[from].color()!==this.state.colorToMove) return; 
    if(squares_copy[from].color()===squares_copy[to].color()) return; 

    let type = squares_copy[from].type();
    let currentMove = this.state.currentMove;

    let history_copy = JSON.parse(JSON.stringify(this.state.history)); 
    history_copy[currentMove+1] = JSON.parse(JSON.stringify(history_copy[currentMove])); 
    history_copy[currentMove+1][from] = null;  
    history_copy[currentMove+1][to] = [type, squares_copy[to].color()]; 
    // let newposition = [...history_copy[currentMove]]; 
    // newposition[from] = null; 
    // newposition[to] = [type, squares_copy[to].color()];
    // history_copy[currentMove+1] = [...newposition];

    squares_copy[to].piece = squares_copy[from].piece;
    squares_copy[from].piece = null; 

    this.setState(prevState => ({
      squares: squares_copy,
      history: history_copy,
      lastMove: [prevState.colorToMove, type, to], 
      currentMove: prevState.currentMove+1,
      colorToMove: this.switchColor()
    }));
  };

  render(){
    let {squares, currentMove} = this.state;
    return(
    	<div className='container'>
	    	<div className="board"> 
          {this.renderSquares(squares)}
	    		{/*<Board onDrop={this.dropHandler} lastMove={this.state.lastMove} moveHistory={this.state.moveHistory} currentMove={this.state.currentMove} />*/}
      	</div>
      		<div className='moves' >
      			<Moves onClick={this.moveClick} lastMove={this.state.lastMove} /> 
      		</div>
      	</div> 
    );
  }   
}

export default App;
