              /*return square 
              ? 
              <div 
                onDragOver={this.dragOverHandler} 
                onDragLeave={this.dragLeaveHandler} 
                onDrop={this.dropHandler} 
                data-square={SQUARES[squares_ids[i]]}
                id={squares_ids[i]}
                data-position={squares_ids[i]}
                key={squares_ids[i]} 
                className='square'> 
                  <div 
                    className={`piece ${this.colorConv(square.color)} ${square.type}`}
                    draggable='true'
                    data-position={squares_ids[i]}
                    onDragStart={this.dragStartHandler}
                    onDragEnd={this.dragEndHandler}
                    onClick={this.handlePieceClick}
                  />
              </div>
              : 
              <div 
                data-square={SQUARES[squares_ids[i]]}
                data-position={squares_ids[i]}
                id={squares_ids[i]}
                onDragOver={this.dragOverHandler} 
                onDragLeave={this.dragLeaveHandler} 
                onClick={this.handleClick} 
                onDrop={this.dropHandler} 
                key={squares_ids[i]} 
                className='square' 
            />;*/}
            return square 
              ? 
              <div 
                onDragOver={this.dragOverHandler} 
                onDragLeave={this.dragLeaveHandler} 
                onDrop={this.dropHandler} 
                data-square={SQUARES[squares_ids[i]]}
                id={squares_ids[i]}
                data-position={squares_ids[i]}
                key={squares_ids[i]} 
                className={`square ${square_classes[c%2]}`}> 
                  <div className='square-bg'>
                    <div className='control-dark' /> 
                    <div className='control-light' />
                  </div> 
                  <div 
                    className={`piece ${this.colorConv(square.color)} ${square.type}`}
                    draggable='true'
                    data-position={squares_ids[i]}
                    onDragStart={this.dragStartHandler}
                    onDragEnd={this.dragEndHandler}
                    onClick={this.handlePieceClick}
                  />  
              </div>
              : 
              <div 
                data-square={SQUARES[squares_ids[i]]}
                data-position={squares_ids[i]}
                id={squares_ids[i]}
                onDragOver={this.dragOverHandler} 
                onDragLeave={this.dragLeaveHandler} 
                onClick={this.handleClick} 
                onDrop={this.dropHandler} 
                key={squares_ids[i]} 
                className='square'> 
                  <div className='square-bg'>
                    <div className='control-dark' /> 
                    <div className='control-light' />
                  </div> 
              </div>


            {/*props['data-square'] = SQUARES[squares_ids[i]]; 
            props['id'] = squares_ids[i];

            if(!square) return <Square props={props} piece={false} />; 
            else{
              pieceProps['className']=`piece ${this.colorConv(square.color)} ${square.type}`; 
              return <Square props={props} piece={true} pieceProps={pieceProps} />; 
            }    
            */}          

          })
        }
        </div>

        <Moves 
          onClick={this.moveClick} 
          selected_move={this.state.selected_move}
          moves={this.state.history}
          colorToMove={colorToMove}
          result={this.state.result}
          >
            <ImageButton 
              button='pgn_button' 
              altText='Enter PGN'
              onClick={this.pgnClick}
            />
            <ImageButton 
              button='play_button' 
              altText='Turn Autoplay on'
              state={this.toggleConvert(this.state.autoplay)}
              onClick={this.handleAutoplayClick}  
              />

          {/*<Button 
            text='Flip Board' 
            onClick={this.handleFlipClick} />*/}
          <ToggleTextButton 
            text={['Hide Pieces', 'Show Pieces']} 
            initialText={0}
            onClick={this.handleHideClick} />
           <ToggleTextButton 
            text={['Hide Board Control', 'Show Board Control']} 
            initialText={0}
            onClick={this.handleControlClick} />
        </Moves>



        {this.state.promotion.from ? 
          <PromotionPrompt 
            onClick={this.promotionHandler}
            from={this.state.promotion.from}
            to={this.state.promotion.to}
            color={this.state.promotion.color}

            />
            : ''
        }
        {this.state.popUp ?  
          <PopUp
             props={this.state.popUp}
          />  
          : ''}

      </div> 
    );
  } 

  componentDidMount(){
    document.addEventListener("keydown", this.keyDownHandler);
    if(this.state.showControl) this.showBoardControl();
  }


  shouldComponentUpdate(nextProps, nextState){
    if(this.state.showPieces!==nextState.showPieces) return false;
    // if(this.state.piece_clicked!==nextState.piece_clicked) return false;
    if(this.state.showControl!==nextState.showControl){
      this.hideBoardControl();
      return false; 
    }
    return true;
  }

  componentDidUpdate() {
    console.log('componentDidUpdate');
    if(this.state.selected_move>0){ // For highlighting the last move
      // console.log(this.state.history.pop());
    }
    if(this.state.showControl) this.showBoardControl();
  }

  /*
  ----------------------------------------------------------------------------------------------------------------------------------
  ---------------------------------------------------- EVENT HANLDERS --------------------------------------------------------------
  ----------------------------------------------------------------------------------------------------------------------------------
  */

  handleEvalClick = () => {    
    // let pgn = "1. d4 d5 2. c4 c6 3. Nf3 Nf6 4. Nc3 e6 5. e3 Nbd7 6. Bd3 dxc4 7. Bxc4 b5 8. Bd3 Bd6 9. O-O O-O 10. Qc2 Bb7 11. a3 Rc8 12. Ng5 c5 13. Nxh7 Ng4 14. f4 cxd4 15. exd4 Bc5 16. Be2 Nde5 17. Bxg4 Bxd4+ 18. Kh1 Nxg4 19. Nxf8 f5 20. Ng6 Qf6 21. h3 Qxg6 22. Qe2 Qh5 23. Qd3 Be3 0-1";
    // this.loadPGN(pgn);

    // if(chess.in_check()) console.log(chess.get_kings());
  }

  keyDownHandler = (e)=>{
    switch(e.keyCode){
      case 39: // Right arrow 
        e.preventDefault();
        if(this.state.selected_move+1<=this.state.fen_history.length-1){ 
