          // document.getElementById('m'+this.state.selected_move+1).classList.add('selected');
        }
        break;
      case 37: // Left arrow 
        e.preventDefault();
        if(this.state.selected_move>=1){
          this.setState(prevState=>({selected_move: prevState.selected_move-1}));
          // this.setState(prevState=>({selected_move: prevState.selected_move-1}), this.showBoardControl());
          // this.removeClassFromAll('selected');
          // document.getElementById('m'+this.state.selected_move-1).classList.add('selected');
        }
        break;
      case 27: // esc 
        e.preventDefault(); 
        if(this.state.popUp){
          this.setState({popUp: false});
        }
        break;
      default: break; 
    }
  }

  dragStartHandler = (e) => { // e: dragEvent, i: original square of dragged piece
