    this.setState(prevState=> ({showControl: !prevState.showControl}));  
  }

  handleGamesClick = () => {
    fetch('http://localhost:3000/test', {method:'GET'})
      .then(res => res.text())
      .then(res => {
        this.loadGames(res, '###');
      });
  }

