  loadPGN(pgn){
    let r = chess.load_pgn(pgn);
    let headers = chess.header(); 
    let board = document.getElementById(BOARD_ID);
    if(headers.Black){
      let content = headers.BlackTitle ? `[${headers.BlackTitle}]` + ' ' + headers.Black : headers.Black; 
      board.dataset.blackplayer = content;
      board.classList.add('blackplayer');
    }else board.classList.remove('blackplayer');
    if(headers.White){
      let content = headers.WhiteTitle ? `[${headers.WhiteTitle}]` + ' ' + headers.White : headers.White; 
      board.dataset.whiteplayer = content;  
      board.classList.add('whiteplayer');
    }else board.classList.remove('whiteplayer');
    let result = headers.Result ? 'Result: ' + headers.Result : '';
    if(headers.Termination) result += ` (${headers.Termination})`; 
    if(r){
      let history = chess.history(); 
      chess.reset();
      let fen_history = [chess.fen()];
      history.forEach(e=>{
        chess.move(e);
        fen_history.push(chess.fen());
      });
      // let fen_history = history.map(e => {
      //   chess.move(e);
      //   return chess.fen();   
      // });
      chess.load(fen_history[0]);
      // this.setState({history, fen_history, loaded_game: pgn, selected_move: 0}, this.showBoardControl());
      this.setState({history, fen_history, loaded_game: pgn, selected_move: 0, result});

    }else console.log(r);
  }

