  getRatio(w, b){
    if(w===0&&b===0) return 0;  
    let sum = w + b; 
    let whiteshare = w/sum*100; 
    return whiteshare; 
  }

  getWeightedRatio(w, b){
    if(w==0) return 0; 
    if(b==0) return 100;
    let sum = w + b; 
    let whiteshare = 100-w/sum*100; 
    return whiteshare; 
  }

