  openPromotionPrompt(from, to, color){
    this.setState({promotion: {from, to, color}}, () => {
      let promotion = document.querySelector('.promotion_prompt'); 
      let rect = document.getElementById(this.state.promotion.to).getBoundingClientRect();
      let color = this.state.promotion.color; 
      promotion.style.left = rect.left+'px';
      promotion.style.top = color=='black' ? `${rect.top-3*SQUARE_SIZE}px` : `${rect.top}px`;
    });
  }

  newMove(from, to, promoteTo = false){
    let fen_history = [...this.state.fen_history]; 
    let history = [...this.state.history];
    let options = {from, to}; 

    if(this.state.selected_move<this.state.history.length){ // Override if a previous move was selected
      history = history.slice(0, this.state.selected_move);
      fen_history = fen_history.slice(0, this.state.selected_move+1); 
    }

    if(promoteTo) options.promotion = promoteTo;
    else{
      if(this.isOnPromotionSquare(to, chess.turn()) && chess.get(from).type == 'p'){
        // options.promotion = 'q';
        if(this.state.promotion.from==null){ 
          this.openPromotionPrompt(from, to, this.colorConv(chess.turn()), this.newMove);
          return;
        }else{
          options.promotion = promoteTo;
        } 
      }
    }

    let res = chess.move(options);
    if(res){
      fen_history.push(chess.fen());
      history.push(res.san);
      this.setState(prevState => (
        {selected_move: prevState.selected_move+1, history, fen_history}
      ));
      return res.san; 
    }else{
      console.log('Error making new move: '+res);
      return false; 
    } 
  }

