import React from 'react'; 
import './Square.css';
import Piece from '../Piece/Piece';

class Square extends React.Component{
	constructor(props){
		super(props); 
		const {row, column, background, piece, onClick, pieceClick} = props; 
		this.state = {
			row: row,
			column: column,
			background: background,
			piece: piece, 
			onClick: onClick, 
			pieceClick: pieceClick,
		}
	}

	handleClick = (e) => {
		// this.setState({background: 'white'}); 
		console.log(`square ${e} clicked`);
	};

	handlePieceClick = (e) => {
		console.log(`piece ${e} clicked`);
	}

	render(){
		let {row, column, background, piece, onClick, pieceClick} = this.state; 
		let classes = 'square '+background;
		let position = [column, row];
		let returnThis = piece 
			? <div className={classes} data-row={row} 
			// onClick={onClick} 
			data-column={column}> 
			<Piece position={position} 
			// onClick={pieceClick} 
			onClick={() => this.handlePieceClick(position)}
			type={piece[0]} color={piece[1]} /> </div>
			: <div className={classes} data-row={row} onClick={()=>this.handleClick('hi')} data-column={column} />;
		return returnThis;
	}

}

export default Square;
