import React from 'react'; 
import './Piece.css';

const typeConv = {
	0: 'pawn', 1:'bishop', 2:'knight', 3:'rook', 4:'queen', 5:'king'
}

let colorConv = number => {
	if(number===1) return 'white';
	else if(number===-1) return 'black'; 
	else return null; 
}
class Piece extends React.Component {

	constructor(props){
		super(props);
		const {position, type, color, onClick} = this.props; 
		console.log(color);
		this.state = {
			position: position, 
			type: type,
			color:color,
			onClick: onClick
		};
	}
	render() {
		// console.log(colorConv(color));
		let pieceClass = `piece ${typeConv[this.state.type]} ${colorConv(this.state.color)}`
		return (
			<div className={pieceClass} onClick={()=>this.state.onClick()} /> 
		);
	}

}

export default Piece;
