import React, {useState} from 'react'; 
import './PGNDialog.css';
import GamesList from './../GamesList/GamesList';


const PGNDialog = ({closePopup}) => {


	const [input, setInput] = useState('');
	const [games, setGames] = useState('');

	function loadStudy(){
		fetch('http://localhost:3001/game?all=true')
	      	.then(res => res.json())
	     	.then(res => {
	        	setGames(<GamesList games={res} closePopup={closePopup} />);
        	});
	}


	function pasteClipboard(){
		console.log(document.execCommand('paste'));
	}

	return (
		<>
			<h1>Import Game</h1> 
				<div className="inputclass">
					<div className="inputtitle">PGN</div>
					<div className="inputbox"> 
						<textarea 
							placeholder="Enter PGN" 
							onChange={(e) => setInput(e.target.value)} 
							value={input}
							// value="https://lichess.org/game/export/6dFspuKY" 
							id="inputpgn" /> 
					</div>
					<div className="inputsubmit">
						{/*<span  
							className='popup_button pointer' 
							value="Paste" 
							onClick={()=>pasteClipboard()}>
							Paste
						</span>*/}

						<span 
							id="popupsubmit" 
							className='pointer popup_button' 
							value="submit" 
							onClick={()=>closePopup(input)}>
							Import
						</span>

					</div>
				</div>

				{/*<div className="inputclass">
					<div className="inputtitle">Lichess</div>
					<div className="inputbox"> 
						<input
							type="text" 
							placeholder="Enter Game ID "  
							id="inputlichess" /> 
					</div>
					<div className="inputsubmit">
						<span 
							id="popupsubmit" 
							className='pointer' 
							value="submit" 
							onClick={()=>closePopup(document.getElementById('inputlichess').value)}>
							Import
						</span>
					</div>
				</div>*/}

				<h2> Famous Games: </h2> 

				{/*<div className="inputclass">
					<div className="inputtitle">Famous Games:</div>
					<div className="inputbox"> 
						<input
							type="text" 
							className="popup_button"
							placeholder="Enter Study ID "  
							id="inputlichessstudy" /> 
					</div>
					<div className="inputsubmit">
						<span 
							id="popupsubmit" 
							className='pointer popup_button' 
							value="submit" 
							onClick={loadStudy}>
							Import
						</span>
					</div>
				</div>*/}


				<div className="collapsed">
					{games}
				</div>
		</>
	);
}

export default PGNDialog;
