  // Returns array with position, but availableMoves-array instead of piece-value for respective squares 
  evalPosition(position){
    console.time('eval');
    let evaluation = Array(position.length);
    position.forEach((square, i) => {
      evaluation[i] = this.availableMoves(i); 
    });
    console.timeEnd('eval'); 
    return evaluation;
  }

  convertBack(row, column){
    return rev_conversion[Number(''+row+column)]; 
  }

  availableMoves(square, position){
    // console.time('moves');
    let moves = [];
    // let position = this.state.positions[this.state.positions.length-1];
    let piece = position[square]; 
    if(piece===0) return 0;

    let [row, column] = conversion[square];
    let multiplier = (piece>0) ? 1 : -1; 
    switch(Math.abs(piece)){

      case 1: { // PAWN-white
        if(piece===1){
          if(row===6 && !this.hasPiece(position[square-16])) moves.push(square-16); // 2 forward
          if(!this.hasPiece(position[square-8])) moves.push(square-8); // 1 forward 

          if(column>=1 && row >=1 && this.hasOpposingPiece(piece, position[this.convertBack(row-1, column-1)])) moves.push(this.convertBack(row-1, column-1)); // up-left-capture 
          if(column<=6 && row >=1 && this.hasOpposingPiece(piece, position[this.convertBack(row-1, column+1)])) moves.push(this.convertBack(row-1, column+1)); // up-right-capture 
        }else{
          if(row===1 && !this.hasPiece(position[square+16])) moves.push(square+16); // 2 forward
          if(!this.hasPiece(position[square+8])) moves.push(square+8); // 1 forward 

          if(column>=1 && row<=6 && this.hasOpposingPiece(piece, position[this.convertBack(row+1, column-1)])) moves.push(this.convertBack(row+1, column-1)); // down-left-capture 
          if(column<=6 && row<=6 && this.hasOpposingPiece(piece, position[this.convertBack(row+1, column+1)])) moves.push(this.convertBack(row+1, column+1)); // down-right-capture 
        }
        break;
      }case 2:{ //BISHOPS 
        let r = row; 
        let left = true; let right = true; 
        let lc = column; let rc = column; 
        for(r=r+1;r<=7;r++){ // UNTEN 
          if(!left && !right) break; 
          if(left){
            lc-=1;
            if(lc<0) left = false; 
            else{
              let current_square = this.convertBack(r, lc);
              let current_piece = position[current_square];
              if(this.hasPiece(current_piece)){
                if(this.hasAlliedPiece(piece, current_piece)) left = false; 
                else{
                  moves.push(current_square); 
                  left = false; 
                }
              }else moves.push(current_square);
            }
          }if(right){
            rc+=1;
            if(rc>7) right = false; 
            else{
              let current_square = this.convertBack(r, rc);
              let current_piece = position[current_square];
              if(this.hasPiece(current_piece)){
                if(this.hasAlliedPiece(piece, current_piece)) right = false; 
                else{
                  moves.push(current_square); 
                  right = false; 
                }
              }else moves.push(current_square);
            }
          }
        }
        r = row; 
        left = true; right = true; 
        lc = column; rc = column; 
        for(r=r-1;r>=0;r--){ // OBEN 
          if(!left && !right) break; 
          if(left){
            lc-=1;
            if(lc<0) left = false; 
            else{
              let current_square = this.convertBack(r, lc);
              let current_piece = position[current_square];
              if(this.hasPiece(current_piece)){
                if(this.hasAlliedPiece(piece, current_piece)) left = false; 
                else{
                  moves.push(current_square); 
                  left = false; 
                }
              }else moves.push(current_square);
            }
          }if(right){
            rc+=1;
            if(rc>7) right = false; 
            else{
              let current_square = this.convertBack(r, rc);
              let current_piece = position[current_square];
              if(this.hasPiece(current_piece)){
                if(this.hasAlliedPiece(piece, current_piece)) right = false; 
                else{
                  moves.push(current_square); 
                  right = false; 
                }
              }else moves.push(current_square);
            }
          }
        }
        break; 
      }case 3:{ //KNIGHTS 
        let current_square;
        if(row+1<=7){ // UNTEN
          current_square = this.convertBack(row+1, column-2); 
          if(column-2>=0 && ( !this.hasPiece(position[current_square]) || this.hasOpposingPiece(piece, position[current_square]))) moves.push(current_square); 
          current_square = this.convertBack(row+1, column+2); 
          if(column+2<=7 && ( !this.hasPiece(position[current_square]) || this.hasOpposingPiece(piece, position[current_square]))) moves.push(current_square); 
          if(row+2<=7){
            current_square = this.convertBack(row+2, column-1); 
            if(column-1>=0 && ( !this.hasPiece(position[current_square]) || this.hasOpposingPiece(piece, position[current_square]))) moves.push(current_square);
            current_square = this.convertBack(row+2, column+1); 
            if(column+1<=7 && ( !this.hasPiece(position[current_square]) || this.hasOpposingPiece(piece, position[current_square]))) moves.push(current_square); 
          }

        }
        if(row-1>=0){ // OBEN
          current_square = this.convertBack(row-1, column-2); 
          if(column-2>=0 && ( !this.hasPiece(position[current_square]) || this.hasOpposingPiece(piece, position[current_square]))) moves.push(current_square); 
          current_square = this.convertBack(row-1, column+2); 
          if(column+2<=7 && ( !this.hasPiece(position[current_square]) || this.hasOpposingPiece(piece, position[current_square]))) moves.push(current_square); 
          if(row-2>=0){
            current_square = this.convertBack(row-2, column-1); 
            if(column-1>=0 && ( !this.hasPiece(position[current_square]) || this.hasOpposingPiece(piece, position[current_square]))) moves.push(current_square);
            current_square = this.convertBack(row-2, column+1); 
            if(column+1<=7 && ( !this.hasPiece(position[current_square]) || this.hasOpposingPiece(piece, position[current_square]))) moves.push(current_square); 
          }

        }
        break;
      }case 4: { //ROOKS 
        for(let r=row-1;r>=0;r--){ //OBEN 
          let current_square = this.convertBack(r, column);
          let current_piece = position[current_square];
          if(this.hasPiece(current_piece)){
            if(this.hasAlliedPiece(current_piece, piece)) break;
            moves.push(current_square);
            break;
          }
          moves.push(current_square);
        }
        for(let r=row+1;r<=7;r++){ //UNTEN
          let current_square = this.convertBack(r, column);
          let current_piece = position[current_square];
          if(this.hasPiece(current_piece)){
            if(this.hasAlliedPiece(current_piece, piece)) break;
            moves.push(current_square);
            break;
          }
          moves.push(current_square);
        }
        for(let c=column+1;c<=7;c++){ //RECHTS
          let current_square = this.convertBack(row, c);
          let current_piece = position[current_square];
          if(this.hasPiece(current_piece)){
            if(this.hasAlliedPiece(current_piece, piece)) break;
            moves.push(current_square);
            break;
          }
          moves.push(current_square);
        }
        for(let c=column-1;c>=0;c--){ //LINKS
          let current_square = this.convertBack(row, c);
          let current_piece = position[current_square];
          if(this.hasPiece(current_piece)){
            if(this.hasAlliedPiece(current_piece, piece)) break;
            moves.push(current_square);
            break;
          }
          moves.push(current_square);
        }
        break; 
      }case 5: { //QUEENS 
        let r = row; 
        let left = true; let right = true; 
        let lc = column; let rc = column; 
        for(r=r+1;r<=7;r++){ // UNTEN 
          if(!left && !right) break; 
          if(left){
            lc-=1;
            if(lc<0) left = false; 
            else{
              let current_square = this.convertBack(r, lc);
              let current_piece = position[current_square];
              if(this.hasPiece(current_piece)){
                if(this.hasAlliedPiece(piece, current_piece)) left = false; 
                else{
                  moves.push(current_square); 
                  left = false; 
                } 
              }else moves.push(current_square);
            }
          }if(right){
            rc+=1;
            if(rc>7) right = false; 
            else{
              let current_square = this.convertBack(r, rc);
              let current_piece = position[current_square];
              if(this.hasPiece(current_piece)){
                if(this.hasAlliedPiece(piece, current_piece)) right = false; 
                else{
                  moves.push(current_square); 
                  right = false; 
                }
              }else moves.push(current_square);
            }
          }
        }
        r = row; 
        left = true; right = true; 
        lc = column; rc = column; 
        for(r=r-1;r>=0;r--){ // OBEN 
          if(!left && !right) break; 
          if(left){
            lc-=1;
            if(lc<0) left = false; 
            else{
              let current_square = this.convertBack(r, lc);
              let current_piece = position[current_square];
              if(this.hasPiece(current_piece)){
                if(this.hasAlliedPiece(piece, current_piece)) left = false; 
                else{
                  moves.push(current_square); 
                  left = false; 
                }
              }else moves.push(current_square);
            }
          }if(right){
            rc+=1;
            if(rc>7) right = false; 
            else{
              let current_square = this.convertBack(r, rc);
              let current_piece = position[current_square];
              if(this.hasPiece(current_piece)){
                if(this.hasAlliedPiece(piece, current_piece)) right = false; 
                else{
                  moves.push(current_square); 
                  right = false; 
                }
              }else moves.push(current_square);
            }
          }
        }
        for(r=row-1;r>=0;r--){ //OBEN 
          let current_square = this.convertBack(r, column);
          let current_piece = position[current_square];
          if(this.hasPiece(current_piece)){
            if(this.hasAlliedPiece(current_piece, piece)) break;
            moves.push(current_square);
            break;
          }
          moves.push(current_square);
        }
        for(r=row+1;r<=7;r++){ //UNTEN
          let current_square = this.convertBack(r, column);
          let current_piece = position[current_square];
          if(this.hasPiece(current_piece)){
            if(this.hasAlliedPiece(current_piece, piece)) break;
            moves.push(current_square);
            break;
          }
          moves.push(current_square);
        }
        for(c=column+1;c<=7;c++){ //RECHTS
          let current_square = this.convertBack(row, c);
          let current_piece = position[current_square];
          if(this.hasPiece(current_piece)){
            if(this.hasAlliedPiece(current_piece, piece)) break;
            moves.push(current_square);
            break;
          }
          moves.push(current_square);
        }
        for(c=column-1;c>=0;c--){ //LINKS
          let current_square = this.convertBack(row, c);
          let current_piece = position[current_square];
          if(this.hasPiece(current_piece)){
            if(this.hasAlliedPiece(current_piece, piece)) break;
            moves.push(current_square);
            break;
          }
          moves.push(current_square);
        }
        break;
      }case 6: { //KING
        let col_r = true;
        let col_l = true;
        let cs; let cp; 
        if(row+1<=7){
          cs = this.convertBack(row+1, column); // 1 unten
          cp = position[cs]; 
          if(!this.hasPiece(cp) || this.hasOpposingPiece(cp, piece)) moves.push(cs);
          if(column+1<=7){
            cs = this.convertBack(row+1, column+1); //unten rechts 
            cp = position[cs];
            if(!this.hasPiece(cp) || this.hasOpposingPiece(cp, piece)) moves.push(cs);
          }else col_r = false; 
          if(column-1>=0){
            cs = this.convertBack(row+1, column-1); // unten links
            cp = position[cs];
            if(!this.hasPiece(cp) || this.hasOpposingPiece(cp, piece)) moves.push(cs);
          }else col_l = false;
        }if(col_r){
          if(column+1<=7){
            cs = this.convertBack(row, column+1);// 1 rechts
            cp = position[cs]; 
            if(!this.hasPiece(cp) || this.hasOpposingPiece(cp, piece)) moves.push(cs);
          }else col_r = false; 
        }if(col_l){
          if(column-1>=0){
            cs = this.convertBack(row, column-1); // 1 links
            cp = position[cs];
            if(!this.hasPiece(cp) || this.hasOpposingPiece(cp, piece)) moves.push(cs);
          }else col_l = false; 
        }if(row-1>=0){
          cs = this.convertBack(row-1, column); // 1 oben
          cp = position[cs]; 
          if(!this.hasPiece(cp) || this.hasOpposingPiece(cp, piece)) moves.push(cs);
          if(col_r){
            cs = this.convertBack(row-1, column+1); // oben rechts
            cp = position[cs]; 
            if(!this.hasPiece(cp) || this.hasOpposingPiece(cp, piece)) moves.push(cs);
          }if(col_l){
            cs = this.convertBack(row-1, column-1); // oben links
            cp = position[cs]; 
            if(!this.hasPiece(cp) || this.hasOpposingPiece(cp, piece)) moves.push(cs);
          }
        }
        break;
      }case 0: return 0;
      default: return 0;
    } 
    // console.timeEnd('moves');
    return moves; 
  }

  dragStartHandler = (e, i) => {
    e.dataTransfer.effectAllowed = "all"
    e.dataTransfer.dropEffect = "move";
    e.dataTransfer.setData("text/plain", i);
    requestAnimationFrame(function () {
      e.target.classList.add('hide');
    });
    let availableMoves = this.availableMoves(i, this.state.positions[this.state.currentMove]); 
    availableMoves.forEach(el => {
      document.getElementById('s'+el).classList.add('moveable');
    });
    this.setState({piece_dragging: availableMoves});
  }

  dragEndHandler = (e) =>{
    e.preventDefault();
    requestAnimationFrame(function () {
        e.target.classList.remove('hide');
      });
  }

  moveClick = (number) => {
    console.log('move clicked',number);
    this.setState(prevState=>({
      selectedMove: number
    }));
  }

  handleClick = (square) => {
    let clicked = this.state.piece_clicked; 
    if(clicked){
      let availableMoves = this.state.availableMoves; 
      if(availableMoves.includes(square)){ 
        this.removeClassFromAll('moveable'); 
        this.removeClassFromAll('active');
        this.setState({piece_clicked: false, availableMoves: null}, this.newMove(clicked, square));
      }else{
        // this.removeClassFromAll('active'); 
        // this.setState({piece_clicked: false })
      }
    }
  }

  handlePieceClick = (square, event) => {
    let clicked = this.state.piece_clicked;
    if(!clicked){
      if(this.getColorFromSquare(square)!==this.state.colorToMove) return; 
      let availableMoves = this.availableMoves(square, this.state.positions[this.state.currentMove]); 
      event.target.parentElement.classList.add('active');
      availableMoves.forEach(el => {
        document.getElementById('s'+el).classList.add('moveable');
      });
      this.setState({piece_clicked: square, availableMoves: availableMoves});
    }else{
      this.removeClassFromAll('active');
      this.removeClassFromAll('moveable');
      if(this.getColorFromSquare(square)===this.state.colorToMove){ 
          event.target.parentElement.classList.add('active');
          let availableMoves = this.availableMoves(square, this.state.positions[this.state.currentMove]); 
          availableMoves.forEach(el => {
            document.getElementById('s'+el).classList.add('moveable');
          });
          this.setState({piece_clicked: square, availableMoves: availableMoves});
      }else{
        let legitmoves = this.state.availableMoves;
        if(legitmoves.includes(square)){
          this.setState({piece_clicked: false, availableMoves: null}, this.newMove(clicked, square));  
        } 
      }
    }
  }

  dropHandler = (e, target) => {
    e.preventDefault();
    let origin = e.dataTransfer.getData('text/plain');
    this.removeClassFromAll('dragover');
    this.removeClassFromAll('moveable');

    let legitmoves = this.state.piece_dragging;
    if(legitmoves.includes(target)) this.setState({piece_dragging: false}, this.newMove(origin, target));
    else this.setState({piece_dragging: false});
    // e.target.classList.remove('dragover');
    // let target = e.target.dataset.position; 
    // let origin = e.dataTransfer.getData("text/plain");
    // this.newMove(origin, target);
  };

  removeClassFromAll(remove){
    document.querySelectorAll('.'+remove).forEach(el=>el.classList.remove(remove));
  }

  switchColor(){ return this.state.colorToMove === 1 ? -1 : 1; }

  getColor(piece){ return piece<0 ? -1 : 1; }

  getColorFromSquare(square){ return this.getColor(this.state.positions[this.state.currentMove][square]); }

  hasPiece(piece){ return Math.abs(piece)>0; }

  squareHasPiece(square){ return this.hasPiece(this.state.positions[this.state.currentMove][square]); }

  squareHasOpposingPiece(square, square2){ return this.getColorFromSquare(square) !== this.getColorFromSquare(square2); }

  hasOpposingPiece(piece1, piece2){ return (this.hasPiece(piece2) && this.getColor(piece1)!==this.getColor(piece2)); }

  hasAlliedPiece(piece1, piece2){ return (this.hasPiece(piece2) && this.getColor(piece1)===this.getColor(piece2)); }

  newMove(from, to){
    let positions = JSON.parse(JSON.stringify(this.state.positions));
    let old_position = Array.from(positions[positions.length-1]);
    let new_position = Array.from(old_position);

    if(this.getColor(old_position[from])!==this.state.colorToMove) return; 
    if(this.hasPiece(old_position[to])){ 
      if(this.getColor(old_position[from])===this.getColor(old_position[to])) return; 
    }
    let moved_piece = old_position[from];
    new_position[to] = new_position[from];
    new_position[from] = 0;
    positions.push(new_position);

    this.setState(prevState=>({
      positions: positions,
      lastMove: [prevState.currentMove, moved_piece, to],
      currentMove: prevState.currentMove+1,
      selectedMove: prevState.selectedMove+1,
      colorToMove: this.switchColor()
    }));
  }
}

export default App;
